import React, { useEffect, useState } from 'react';
import api from '../services/api';

const PaymentsPage = () => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await api.get('/payments/user', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPayments(res.data);
      } catch (err) {
        console.error('âŒ Failed to fetch payments:', err.response?.data || err.message);
      }
    };

    fetchPayments();
  }, []);

  const handleStripeCheckout = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await api.post('/payments/create-session', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      window.location.href = res.data.url; // redirect to Stripe
    } catch (err) {
      console.error("Stripe session error:", err.response?.data || err.message);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>ðŸ’³ Payments</h2>

      {payments.length === 0 ? (
        <p>No payments found.</p>
      ) : (
        <ul>
          {payments.map((p, index) => (
            <li key={p.payment_id || index}>
              <strong>Amount:</strong> ${p.amount} â€”{' '}
              <strong>Date:</strong> {new Date(p.paid_date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}

      <button
        onClick={handleStripeCheckout}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          background: "black",
          color: "white",
          cursor: "pointer"
        }}
      >
        Pay Now with Stripe
      </button>
    </div>
  );
};

export default PaymentsPage;
