import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);

      const role = parsedUser.role?.toLowerCase();
      switch (role) {
        case "tenant":
          navigate("/tenant");
          break;
        case "landlord":
          navigate("/landlord");
          break;
        case "admin":
          navigate("/admin-panel");
          break;
        default:
          navigate("/unauthorized");
      }
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-blue-400 to-purple-500 px-4 text-center">
      <h1 className="text-5xl font-bold text-white mb-2">Smart Property System</h1>
      <p className="text-white/90 mb-6 text-lg">Welcome! Please choose an option below.</p>

      {user && <p className="text-white mb-4 text-lg">ðŸ‘‹ Welcome back, {user.name}</p>}

      <div className="flex flex-wrap gap-4 justify-center">
        {!user ? (
          <>
            <Link to="/login">
              <button className="px-6 py-3 bg-white text-blue-600 font-semibold rounded-lg shadow hover:bg-blue-50 transition">
                Login
              </button>
            </Link>
            <Link to="/register">
              <button className="px-6 py-3 bg-white text-purple-600 font-semibold rounded-lg shadow hover:bg-purple-50 transition">
                Register
              </button>
            </Link>
          </>
        ) : (
          <button
            className="px-6 py-3 bg-red-600 text-white font-semibold rounded-lg shadow hover:bg-red-700 transition"
            onClick={handleLogout}
          >
            Logout
          </button>
        )}
      </div>
    </div>
  );
};

export default Home;
