import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function LandlordDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);

  useEffect(() => {
    if (!user || user.role?.toLowerCase() !== 'landlord') {
      navigate('/unauthorized');
      return;
    }

    Promise.all([
      api.get('/properties/landlord'),
      api.get(`/leases/landlord-tenants/${user.id}`),
      api.get(`/payments/landlord/${user.id}`),
      api.get(`/maintenance/landlord/${user.id}`)
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord dashboard:", err.response?.data || err.message)
      );
  }, [user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  // Quick Stats
  const totalProperties = properties.length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);
  const openRequests = maintenance.filter(m => m.status !== 'completed').length;

  return (
    <div style={styles.page}>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* Quick Stats Bar */}
        <div style={styles.statsBar}>
          <div style={styles.stat}><strong>üè† Properties:</strong> {totalProperties}</div>
          <div style={styles.stat}><strong>üë• Tenants:</strong> {totalTenants}</div>
          <div style={styles.stat}><strong>üí≥ Rent Collected:</strong> ${totalRentCollected.toFixed(2)}</div>
          <div style={styles.stat}><strong>üõ†Ô∏è Open Requests:</strong> {openRequests}</div>
        </div>

        {/* Grid of Cards */}
        <div style={styles.grid}>
          {/* Properties */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üè† Properties</h3>
            {properties.length === 0 ? <p>No properties found.</p> : (
              <ul style={styles.list}>
                {properties.map(p => (
                  <li key={p.property_id}>
                    <strong>{p.address}</strong> ‚Äî {p.city}, {p.state}  
                    <div>Status: {p.status}</div>
                    <div>Rent: ${p.rent_amount}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Tenants */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üë• Tenants</h3>
            {tenants.length === 0 ? <p>No tenants assigned.</p> : (
              <ul style={styles.list}>
                {tenants.map(t => (
                  <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Payments */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üí≥ Payments</h3>
            {payments.length === 0 ? <p>No payments recorded.</p> : (
              <ul style={styles.list}>
                {payments.map(pay => (
                  <li key={pay.payment_id}>
                    {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Maintenance */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üõ†Ô∏è Maintenance Requests</h3>
            {maintenance.length === 0 ? <p>No maintenance requests.</p> : (
              <ul style={styles.list}>
                {maintenance.map(m => (
                  <li key={m.request_id}>
                    {m.description} ‚Äî {m.status} ({new Date(m.created_at).toLocaleDateString()})
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

/* Styles */
const styles = {
  page: { background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' },
  navbar: { background: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem 2rem', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', position: 'sticky', top: 0, zIndex: 1000 },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: { background: 'transparent', border: '1px solid #fff', color: '#fff', cursor: 'pointer', padding: '0.4rem 0.8rem', borderRadius: '6px' },
  logoutButton: { background: '#dc3545', border: 'none', color: '#fff', padding: '0.5rem 1rem', borderRadius: '6px', cursor: 'pointer' },
  container: { padding: '2rem', maxWidth: '1200px', margin: '0 auto' },
  heading: { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' },
  role: { fontSize: '1rem', marginBottom: '2rem', color: '#666' },
  statsBar: { display: 'flex', justifyContent: 'space-around', backgroundColor: '#f3f4f6', padding: '1rem', borderRadius: '10px', marginBottom: '2rem', boxShadow: '0 2px 6px rgba(0,0,0,0.05)' },
  stat: { fontSize: '1.1rem', color: '#333' },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: '1.5rem' },
  card: { backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.08)', padding: '1.5rem' },
  sectionTitle: { fontSize: '1.25rem', marginBottom: '1rem', color: '#333', borderBottom: '1px solid #ddd', paddingBottom: '0.5rem' },
  list: { listStyle: 'none', padding: 0, margin: 0 }
};
