import { useNavigate } from 'react-router-dom';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';

export default function Profile({ user }) {
  const navigate = useNavigate();
  const userId = user?.id ?? user?.user_id;

  if (!user) {
    return (
      <div style={styles.page}>
        <div style={styles.card}>
          <h2 style={styles.heading}>Tenant Profile</h2>
          <p style={styles.empty}>No user data found.</p>
          <button style={styles.backButton} onClick={() => navigate('/tenant')}>
            ← Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2 style={styles.heading}>Tenant Profile</h2>
        <p><strong>Name:</strong> {user.name || 'N/A'}</p>
        <p><strong>Email:</strong> {user.email || 'N/A'}</p>
        <p><strong>Role:</strong> {user.role || 'tenant'}</p>
      </div>

      <div style={styles.card}>
        <h3 style={styles.sectionTitle}>Lease Info</h3>
        <TenantLeaseCard userId={userId} />
      </div>

      <div style={styles.card}>
        <h3 style={styles.sectionTitle}>Payment History</h3>
        <TenantPayments userId={userId} />
        {(user.role === 'tenant' || user.role === 'landlord') && (
          <button style={styles.button} onClick={() => navigate('/payments')}>
            Make a Payment
          </button>
        )}
      </div>

      <button style={styles.backButton} onClick={() => navigate('/tenant')}>
        ← Back to Dashboard
      </button>
    </div>
  );
}

const styles = {
  page: {
    padding: '2rem',
    fontFamily: 'Segoe UI, sans-serif',
    backgroundColor: '#f0f2f5',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    padding: '1.5rem',
    width: '100%',
    maxWidth: '600px',
    marginBottom: '2rem'
  },
  heading: {
    fontSize: '2rem',
    marginBottom: '1rem',
    color: '#222'
  },
  sectionTitle: {
    fontSize: '1.25rem',
    marginBottom: '1rem',
    color: '#333',
    borderBottom: '1px solid #ddd',
    paddingBottom: '0.5rem'
  },
  button: {
    marginTop: '1rem',
    padding: '0.5rem 1rem',
    backgroundColor: '#0078D4',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  backButton: {
    background: 'transparent',
    border: 'none',
    color: '#0078D4',
    cursor: 'pointer',
    fontSize: '1rem',
    textDecoration: 'underline'
  },
  empty: {
    color: '#888',
    fontStyle: 'italic',
    marginBottom: '1rem'
  }
};
