import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AdminPanel() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')); // assumes user is stored on login

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      navigate('/unauthorized'); // redirect if not admin
    }
  }, [user, navigate]);

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Admin Panel</h2>
      <ul style={listStyle}>
        <li><button style={buttonStyle} onClick={() => navigate('/properties')}>Manage Properties</button></li>
        <li><button style={buttonStyle} onClick={() => navigate('/tenants')}>View Tenants</button></li>
        <li><button style={buttonStyle} onClick={() => navigate('/maintenance')}>Review Maintenance Requests</button></li>
        <li><button style={buttonStyle} onClick={() => navigate('/passcodes')}>Access Smart Passcodes</button></li>
      </ul>
    </div>
  );
}

const containerStyle = {
  padding: '2rem',
  fontFamily: 'Segoe UI, sans-serif',
  backgroundColor: '#f4f6f8',
  minHeight: '100vh',
  textAlign: 'center'
};

const headingStyle = {
  fontSize: '2rem',
  marginBottom: '1.5rem',
  color: '#333'
};

const listStyle = {
  listStyleType: 'none',
  padding: 0,
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  alignItems: 'center'
};

const buttonStyle = {
  padding: '0.75rem 1.5rem',
  fontSize: '1rem',
  borderRadius: '6px',
  backgroundColor: '#0078D4',
  color: '#fff',
  border: 'none',
  cursor: 'pointer',
  width: '250px',
  textAlign: 'center'
};
