import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LeasePhotosForm = ({ leaseId, token }) => {
  const [photos, setPhotos] = useState([]);
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch photos for this lease
  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const res = await axios.get(`/api/lease-photos/${leaseId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPhotos(res.data);
      } catch (err) {
        console.error('Error fetching photos:', err);
      }
    };
    fetchPhotos();
  }, [leaseId, token]);

  // Handle file upload
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      const res = await axios.post(`/api/lease-photos/upload/${leaseId}`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setPhotos([...photos, res.data.photo]); // append new photo
      setFile(null);
    } catch (err) {
      console.error('Upload error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.card}>
      <h3 style={styles.sectionTitle}>Lease Photos</h3>

      {/* Upload form */}
      <form onSubmit={handleUpload} style={styles.form}>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <button type="submit" disabled={loading || !file}>
          {loading ? 'Uploading...' : 'Upload Photo'}
        </button>
      </form>

      {/* Display photos */}
      {photos.length === 0 ? (
        <p style={styles.empty}>ðŸ“· No lease photos available.</p>
      ) : (
        <div style={styles.photoGrid}>
          {photos.filter(p => p.is_visible_to_tenant).map((p, index) => (
            <figure key={p.id} style={styles.photoWrapper}>
              {/* âœ… alt text fixed: no redundant "photo" */}
              <img
  src={p.url}
  alt={`Uploaded ${p.uploaded_at ? new Date(p.uploaded_at).toLocaleDateString() : 'N/A'}`}
  style={styles.photo}
/>
              <figcaption style={styles.caption}>
                Uploaded: {p.uploaded_at ? new Date(p.uploaded_at).toLocaleDateString() : 'N/A'}
              </figcaption>
            </figure>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  card: { padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' },
  sectionTitle: { marginBottom: '1rem' },
  form: { marginBottom: '1rem' },
  empty: { color: '#888', fontStyle: 'italic', textAlign: 'center' },
  photoGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
    gap: '1rem'
  },
  photoWrapper: { textAlign: 'center' },
  photo: {
    width: '150px',
    height: '100px',
    objectFit: 'cover',
    borderRadius: '8px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
  },
  caption: { fontSize: '0.8rem', color: '#666', marginTop: '0.25rem' }
};

export default LeasePhotosForm;
