import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/smart-passcodes', {
        lease_id: leaseId,
        user_id: userId,
        expires_at: expiresAt || null
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
    } catch (err) {
      console.error('‚ùå Passcode create error:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={form}>
      <h2 style={title}>üîê Generate Smart Passcode</h2>

      <div style={grid}>
        <div style={field}>
          <label style={label}>Lease</label>
          <select value={leaseId} onChange={(e) => setLeaseId(e.target.value)} style={input}>
            <option value="">Select Lease</option>
            {leases.map(l => (
              <option key={l.lease_id} value={l.lease_id}>
                Lease #{l.lease_id} ‚Äì Property {l.property_id}
              </option>
            ))}
          </select>
        </div>

        <div style={field}>
          <label style={label}>User</label>
          <select value={userId} onChange={(e) => setUserId(e.target.value)} style={input}>
            <option value="">Select User</option>
            {users.map(u => (
              <option key={u.user_id} value={u.user_id}>
                {u.name || `User #${u.user_id}`}
              </option>
            ))}
          </select>
        </div>

        <div style={field}>
          <label style={label}>Expiry (optional)</label>
          <input
            type="datetime-local"
            value={expiresAt}
            onChange={(e) => setExpiresAt(e.target.value)}
            style={input}
          />
        </div>
      </div>

      <button type="submit" style={button}>Generate Passcode</button>
    </form>
  );
};

/* üé® Styles */
const form = {
  background: '#fff',
  padding: '2rem',
  borderRadius: '12px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
  maxWidth: '800px',
  margin: '2rem auto',
  display: 'flex',
  flexDirection: 'column',
  gap: '1.5rem'
};

const title = { fontSize: '1.5rem', fontWeight: '600', marginBottom: '0.5rem' };

const grid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
  gap: '1rem'
};

const field = { display: 'flex', flexDirection: 'column' };
const label = { fontSize: '0.9rem', fontWeight: '500', marginBottom: '0.4rem' };
const input = {
  padding: '0.6rem',
  border: '1px solid #ccc',
  borderRadius: '8px',
  fontSize: '0.95rem'
};
const button = {
  alignSelf: 'flex-start',
  padding: '0.7rem 1.2rem',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600',
  fontSize: '0.95rem',
  cursor: 'pointer'
};

export default SmartPasscodeForm;
