import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const lease_id = Number(leaseId);
    const user_id = Number(userId);

    if (!lease_id || !user_id) {
      setError('Please select a valid Lease and User.');
      return;
    }

    try {
      const res = await axios.post(
        '/api/smart-passcodes',
        {
          lease_id,
          user_id,
          expires_at: expiresAt || null
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }
      );

      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
      setSuccess('Passcode generated successfully!');
    } catch (err) {
      console.error('Create passcode error:', err);
      setError('Failed to generate passcode. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <h3 style={styles.title}>Generate Smart Passcode</h3>

      {(error || success) && (
        <div style={error ? styles.errorBanner : styles.successBanner}>
          {error || success}
        </div>
      )}

      <div style={styles.row}>
        <div style={styles.field}>
          <label style={styles.label}>Lease</label>
          <select
            value={leaseId}
            onChange={(e) => setLeaseId(e.target.value)}
            style={styles.select}
          >
            <option value="">Select Lease</option>
            {leases
              .filter(l => l.lease_id)
              .sort((a, b) => a.lease_id - b.lease_id)
              .map((l) => (
                <option key={l.lease_id} value={l.lease_id}>
                  Lease #{l.lease_id} â€” Property {l.property_id}
                </option>
              ))}
          </select>
        </div>

        <div style={styles.field}>
          <label style={styles.label}>User</label>
          <select
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            style={styles.select}
          >
            <option value="">Select User</option>
            {users
              .filter(u => u.user_id)
              .sort((a, b) => a.user_id - b.user_id)
              .map((u) => (
                <option key={u.user_id} value={u.user_id}>
                  {u.name || `User #${u.user_id}`}
                </option>
              ))}
          </select>
        </div>
      </div>

      <div style={styles.field}>
        <label style={styles.label}>Expiry (optional)</label>
        <input
          type="datetime-local"
          value={expiresAt}
          onChange={(e) => setExpiresAt(e.target.value)}
          style={styles.input}
        />
      </div>

      <button type="submit" style={styles.button}>
        Generate Passcode
      </button>
    </form>
  );
};

// ---------- Styles ----------
const styles = {
  form: {
    background: '#ffffff',
    padding: '1.75rem',
    borderRadius: '14px',
    boxShadow: '0 3px 12px rgba(0, 0, 0, 0.06)',
    display: 'flex',
    flexDirection: 'column',
    gap: '1.3rem',
    maxWidth: '600px',
  },
  title: {
    fontSize: '1.35rem',
    fontWeight: '600',
    marginBottom: '0.5rem',
  },
  row: {
    display: 'flex',
    gap: '1rem',
    flexWrap: 'wrap',
  },
  field: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    minWidth: '230px',
  },
  label: {
    marginBottom: '0.45rem',
    fontWeight: '500',
    fontSize: '0.93rem',
    color: '#333',
  },
  input: {
    padding: '0.6rem 0.75rem',
    borderRadius: '8px',
    border: '1px solid #ced4da',
    fontSize: '0.95rem',
    transition: '0.2s',
  },
  select: {
    padding: '0.6rem 0.75rem',
    borderRadius: '8px',
    border: '1px solid #ced4da',
    fontSize: '0.95rem',
    background: '#fff',
    cursor: 'pointer',
    transition: '0.2s',
  },
  button: {
    padding: '0.7rem 1.4rem',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontWeight: '600',
    fontSize: '0.95rem',
    cursor: 'pointer',
    transition: '0.2s',
  },

  errorBanner: {
    background: '#ffe1e1',
    border: '1px solid #ffb2b2',
    color: '#d63031',
    padding: '0.75rem 1rem',
    borderRadius: '8px',
    fontWeight: '500',
  },
  successBanner: {
    background: '#e8ffe9',
    border: '1px solid #b8f5c2',
    color: '#1a8f3e',
    padding: '0.75rem 1rem',
    borderRadius: '8px',
    fontWeight: '500',
  },
};

export default SmartPasscodeForm;
