import React from 'react';

const MaintenanceList = ({ requests, onUpdate, onDelete, user }) => {
  const getStatusStyle = (status) => {
    switch (status) {
      case 'open':
        return { backgroundColor: '#ffc107', color: '#000', padding: '0.25rem 0.5rem', borderRadius: '4px' };
      case 'in_progress':
        return { backgroundColor: '#17a2b8', color: '#fff', padding: '0.25rem 0.5rem', borderRadius: '4px' };
      case 'closed':
        return { backgroundColor: '#28a745', color: '#fff', padding: '0.25rem 0.5rem', borderRadius: '4px' };
      default:
        return { backgroundColor: '#6c757d', color: '#fff', padding: '0.25rem 0.5rem', borderRadius: '4px' };
    }
  };

  return (
    <div style={listStyle}>
      <h3>üõ†Ô∏è Maintenance Requests</h3>
      {requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        requests.map((req) => (
          <div key={req.request_id} style={cardStyle}>
            <p><strong>Property:</strong> #{req.property_id}</p>
            <p><strong>Tenant:</strong> #{req.user_id}</p>
            <p><strong>Description:</strong> {req.description}</p>
            <p>
              <strong>Status:</strong>{' '}
              <span style={getStatusStyle(req.status)}>{req.status}</span>
            </p>
            <p><strong>Created:</strong> {new Date(req.created_at).toLocaleString()}</p>

            {['admin','landlord'].includes(user?.role) && (
              <div style={buttonRow}>
                <button onClick={() => onUpdate(req.request_id, { status: 'in_progress' })}>Mark In Progress</button>
                <button onClick={() => onUpdate(req.request_id, { status: 'closed' })}>Close</button>
                {user?.role === 'admin' && (
                  <button onClick={() => onDelete(req.request_id)}>Delete</button>
                )}
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

const listStyle = { marginBottom: '2rem' };
const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '8px',
  padding: '1rem',
  marginBottom: '1rem',
  backgroundColor: '#fff'
};
const buttonRow = { display: 'flex', gap: '0.5rem', marginTop: '0.5rem' };

export default MaintenanceList;
