import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  // Load user + reminders
  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (stored) {
      const parsed = JSON.parse(stored);
      setUser(parsed);

      if (parsed.role !== "tenant") navigate("/unauthorized");

      api.get(`/user-reminders/${parsed.id}`).then(res => setReminders(res.data));
    } else {
      navigate("/login");
    }
  }, [navigate]);

  // Load passcodes when lease is available
  useEffect(() => {
    if (lease?.lease_id) {
      api.get(`/smart-passcodes/${lease.lease_id}`).then(res => setPasscodes(res.data));
    }
  }, [lease]);

  useEffect(() => {
    console.log("üì• Dashboard Lease:", lease);
  }, [lease]);

  const handlePaymentClick = async () => {
    try {
      const token = localStorage.getItem("token");

      const res = await api.post(
        "/payments/create-session",
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.id,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      console.error("‚ùå Payment error:", err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    onLogout?.();
    navigate("/login");
  };

  return (
    <div style={styles.page}>
      {/* üîµ NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Tenant Portal</div>
        <div style={styles.navRight}>
          {location.pathname !== "/tenant/profile" && (
            <button style={styles.navButton} onClick={() => navigate("/tenant/profile")}>
              Profile
            </button>
          )}
          {location.pathname !== "/tenant" && (
            <button style={styles.navButton} onClick={() => navigate("/tenant")}>
              Dashboard
            </button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {/* üîµ MAIN CONTENT */}
      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* Debug */}
        {lease && (
          <div style={styles.debugBox}>
            <h4>Debug Lease Object</h4>
            <pre>{JSON.stringify(lease, null, 2)}</pre>
          </div>
        )}

        {/* Tenant Basic Info */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
        </div>

        {/* Lease Info */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Lease Info</h3>

          {user?.id && (
            <TenantLeaseCard user={user} onLeaseLoaded={setLease} />
          )}

          {lease && (
            <div style={{ marginTop: "1rem" }}>
              <p><strong>Property ID:</strong> {lease.property_id}</p>
              <p><strong>Start Date:</strong> {new Date(lease.start_date).toLocaleDateString()}</p>
              <p><strong>End Date:</strong> {new Date(lease.end_date).toLocaleDateString()}</p>
              <p><strong>Rent:</strong> ${lease.rent_amount}</p>
            </div>
          )}
        </div>

        {/* Payments */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Payment History</h3>
          {user?.id && <TenantPayments userId={user.id} />}
          <button style={styles.button} onClick={handlePaymentClick}>
            Make a Payment
          </button>
        </div>

        {/* Lease Photos + Documents */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Lease Photos & Documents</h3>

          {lease ? (
            <>
              <LeasePhotosForm
                leaseId={lease.lease_id}
                token={localStorage.getItem("token")}
                role={user?.role}
              />

              <DocumentsForm
                leaseId={lease.lease_id}
                userId={user?.id}
                role={user?.role}
              />
            </>
          ) : (
            <p>Loading lease...</p>
          )}
        </div>

        {/* Reminders */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Reminders</h3>
          <ul>
            {reminders.map(r => (
              <li key={r.reminder_id}>
                <strong>{r.type.toUpperCase()}:</strong> {r.message} ‚Äî {new Date(r.remind_at).toLocaleDateString()}
              </li>
            ))}
          </ul>
        </div>

        {/* Smart Passcodes */}
        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Smart Passcodes</h3>
          <ul>
            {passcodes.map(pc => (
              <li key={pc.passcode_id}>
                Code: {pc.passcode} ‚Äî Expires: {new Date(pc.expires_at).toLocaleString()}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: { backgroundColor: "#f0f2f5", minHeight: "100vh" },
  navbar: {
    background: "#0078D4",
    color: "#fff",
    padding: "1rem 2rem",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  navLeft: { fontWeight: "bold", fontSize: "1.2rem" },
  navRight: { display: "flex", gap: "1rem" },
  navButton: { background: "none", color: "#fff", border: "none", cursor: "pointer" },
  logoutButton: {
    background: "#dc3545",
    border: "none",
    color: "#fff",
    padding: "0.5rem 1rem",
    borderRadius: "6px",
    cursor: "pointer",
  },
  container: { padding: "2rem", display: "flex", flexDirection: "column", alignItems: "center" },
  heading: { fontSize: "2rem", marginBottom: "0.25rem" },
  role: { color: "#666", marginBottom: "2rem" },
  card: {
    background: "#fff",
    borderRadius: "8px",
    padding: "1.5rem",
    width: "100%",
    maxWidth: "600px",
    marginBottom: "2rem",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  },
  sectionTitle: { fontSize: "1.25rem", marginBottom: "1rem", borderBottom: "1px solid #ddd" },
  button: {
    backgroundColor: "#0078D4",
    color: "#fff",
    padding: "0.5rem 1rem",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    marginTop: "1rem",
  },
  debugBox: {
    background: "#fee",
    padding: "1rem",
    width: "100%",
    maxWidth: "600px",
    marginBottom: "2rem",
  },
};

export default TenantDashboard;
