import React from 'react';

const MaintenanceList = ({ requests, onUpdate, onDelete, user }) => {
  if (!requests || requests.length === 0) {
    return (
      <div style={emptyState}>
        <p style={{ fontSize: '1rem', color: '#666' }}>No maintenance requests found.</p>
      </div>
    );
  }

  const handleStatusChange = (id, status) => {
    onUpdate(id, { status });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      onDelete(id);
    }
  };

  const badgeStyle = (status) => {
    const colors = {
      open: '#f39c12',
      in_progress: '#3498db',
      closed: '#2ecc71'
    };
    return {
      backgroundColor: colors[status] || '#ccc',
      color: '#fff',
      padding: '0.3rem 0.6rem',
      borderRadius: '4px',
      fontSize: '0.8rem',
      fontWeight: '500',
      textTransform: 'capitalize',
      display: 'inline-block',
      width: 'fit-content'
    };
  };

  const buttonStyle = {
    padding: '0.5rem 1rem',
    border: 'none',
    borderRadius: '4px',
    fontWeight: '500',
    cursor: 'pointer',
    fontSize: '0.85rem',
    transition: 'background-color 0.2s ease'
  };

  const btnInProgress = { ...buttonStyle, backgroundColor: '#3498db', color: '#fff' };
  const btnClose = { ...buttonStyle, backgroundColor: '#2ecc71', color: '#fff' };
  const btnDelete = { ...buttonStyle, backgroundColor: '#e74c3c', color: '#fff' };

  return (
    <div style={{ marginTop: '1rem' }}>
      {requests.map((r) => (
        <div key={r.request_id} style={card}>
          <div><strong>Property:</strong> #{r.property_id}</div>
          <div><strong>Tenant:</strong> #{r.user_id}</div>
          <div><strong>Description:</strong> {r.description}</div>
          <div><strong>Status:</strong> <span style={badgeStyle(r.status)}>{r.status}</span></div>
          <div><strong>Created:</strong> {new Date(r.created_at).toLocaleString()}</div>

          {['admin','landlord'].includes(user?.role) && (
            <div style={actions}>
              {r.status !== 'in_progress' && (
                <button style={btnInProgress} onClick={() => handleStatusChange(r.request_id, 'in_progress')}>
                  Mark In Progress
                </button>
              )}
              {r.status !== 'closed' && (
                <button style={btnClose} onClick={() => handleStatusChange(r.request_id, 'closed')}>
                  Close
                </button>
              )}
              <button style={btnDelete} onClick={() => handleDelete(r.request_id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

/* ðŸŽ¨ Styles */
const card = {
  backgroundColor: '#fff',
  borderRadius: '10px',
  padding: '1rem',
  marginBottom: '1.5rem',
  boxShadow: '0 2px 6px rgba(0,0,0,0.08)',
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem'
};

const actions = {
  display: 'flex',
  gap: '0.5rem',
  marginTop: '0.75rem',
  flexWrap: 'wrap'
};

const emptyState = {
  padding: '2rem',
  textAlign: 'center',
  backgroundColor: '#fefefe',
  borderRadius: '8px',
  boxShadow: '0 1px 4px rgba(0,0,0,0.05)'
};

export default MaintenanceList;
