import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function LandlordDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || user.role?.toLowerCase() !== 'landlord') {
      navigate('/unauthorized');
      return;
    }

    Promise.all([
      api.get('/properties/landlord/all'),
      api.get(`/leases/landlord-tenants/${user.id}`),
      api.get(`/payments/landlord/${user.id}`),
      api.get(`/maintenance/landlord/${user.id}`)
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        console.log("üì¶ Dashboard properties response:", propsRes.data);
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord dashboard:", err.response?.data || err.message)
      )
      .finally(() => setLoading(false));
  }, [user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  // Quick Stats
  const totalProperties = properties.length;
  const ownedProperties = properties.filter(p => p.is_owned === 1).length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);
  const openRequests = maintenance.filter(m => m.status !== 'completed').length;

  return (
    <div>
      <nav>
        <div>üè† Landlord Portal</div>
        <div>
          <button onClick={() => navigate('/landlord')}>Dashboard</button>
          <button onClick={() => navigate('/landlord/properties')}>Properties</button>
          <button onClick={() => navigate('/landlord/tenants')}>Tenants</button>
          <button onClick={() => navigate('/landlord/payments')}>Payments</button>
          <button onClick={() => navigate('/landlord/maintenance')}>Maintenance</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <h2>Welcome, {user?.name}</h2>
      <p>Role: {user?.role}</p>

      {/* Quick Stats */}
      <div>
        <div>üè† Properties: {loading ? '...' : totalProperties} (Owned: {loading ? '...' : ownedProperties})</div>
        <div>üë• Tenants: {loading ? '...' : totalTenants}</div>
        <div>üí≥ Rent Collected: {loading ? '...' : `$${totalRentCollected.toFixed(2)}`}</div>
        <div>üõ†Ô∏è Open Requests: {loading ? '...' : openRequests}</div>
      </div>

      {/* Properties Card */}
      <div>
        <h3>üè† Properties</h3>
        {loading ? (
          <p>Loading properties...</p>
        ) : properties && properties.length > 0 ? (
          <ul>
            {properties.slice(0, 3).map(p => (
              <li key={p.property_id}>
                <strong>{p.address}</strong> ‚Äî {p.city}, {p.state}
                <div>Status: {p.status}</div>
                <div>Rent: ${p.rent_amount}</div>
                {p.is_owned === 1 && <div>‚úÖ Your Property</div>}
              </li>
            ))}
          </ul>
        ) : (
          <p>No properties found in the system.</p>
        )}
        <button onClick={() => navigate('/landlord/properties')}>View All Properties</button>
      </div>

      {/* Tenants Card */}
      <div>
        <h3>üë• Tenants</h3>
        {loading ? (
          <p>Loading tenants...</p>
        ) : tenants.length > 0 ? (
          <ul>
            {tenants.slice(0, 3).map(t => (
              <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
            ))}
          </ul>
        ) : (
          <p>No tenants assigned.</p>
        )}
        <button onClick={() => navigate('/landlord/tenants')}>View All Tenants</button>
      </div>

      {/* Payments Card */}
      <div>
        <h3>üí≥ Payments</h3>
        {loading ? (
          <p>Loading payments...</p>
        ) : payments.length > 0 ? (
          <ul>
            {payments.slice(0, 3).map(pay => (
              <li key={pay.payment_id}>
                {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
              </li>
            ))}
          </ul>
        ) : (
          <p>No payments recorded.</p>
        )}
        <button onClick={() => navigate('/landlord/payments')}>View All Payments</button>
      </div>

      {/* Maintenance Card */}
      <div>
        <h3>üõ†Ô∏è Maintenance Requests</h3>
        {loading ? (
          <p>Loading requests...</p>
        ) : maintenance.length > 0 ? (
          <ul>
            {maintenance.slice(0, 3).map(m => (
              <li key={m.request_id}>
                {m.description} ‚Äî {m.status} ({new Date(m.created_at).toLocaleDateString()})
              </li>
            ))}
          </ul>
        ) : (
          <p>No maintenance requests.</p>
        )}
        <button onClick={() => navigate('/landlord/maintenance')}>View All Requests</button>
      </div>
    </div>
  );
}


const styles = {
  page: {
    background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif'
  },
  navbar: {
    background: '#2563eb',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    position: 'sticky',
    top: 0,
    zIndex: 1000
  },
  navLeft: {
    fontWeight: 'bold',
    fontSize: '1.2rem'
  },
  navRight: {
    display: 'flex',
    gap: '1rem'
  },
  navButton: {
    background: 'transparent',
    border: '1px solid #fff',
    color: '#fff',
    cursor: 'pointer',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    transition: 'background 0.2s ease'
  },
  logoutButton: {
    background: '#dc3545',
    border: 'none',
    color: '#fff',
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.2s ease'
  },
  container: {
    padding: '2rem',
    maxWidth: '1200px',
    margin: '0 auto'
  },
  heading: {
    fontSize: '2rem',
    marginBottom: '0.25rem',
    color: '#222'
  },
  role: {
    fontSize: '1rem',
    marginBottom: '2rem',
    color: '#666'
  },
  statsBar: {
    display: 'flex',
    justifyContent: 'space-around',
    backgroundColor: '#f3f4f6',
    padding: '1rem',
    borderRadius: '10px',
    marginBottom: '2rem',
    boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
  },
  stat: {
    fontSize: '1.1rem',
    color: '#333'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '1.5rem'
  },
