import React, { useEffect, useState, useCallback } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import api from "../services/api";
import TenantLeaseCard from "../components/TenantLeaseCard";
import TenantPayments from "../components/TenantPayments";
import LeasePhotosForm from "../components/LeasePhotosForm";
import DocumentsForm from "../components/DocumentsForm";

const TenantDashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = useCallback(() => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    if (onLogout) onLogout();
    navigate("/login");
  }, [navigate, onLogout]);

  // Load user + reminders
  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (!stored) return;

    const parsed = JSON.parse(stored);
    setUser(parsed);

    if (parsed.role !== "tenant") navigate("/unauthorized");

    api
      .get(`/user-reminders/${parsed.user_id}`)
      .then((res) => setReminders(res.data))
      .catch((err) => {
        const msg = err.response?.data?.message;
        if (msg === "Token expired") handleLogout();
      });
  }, [navigate, handleLogout]);

  // Load lease
  useEffect(() => {
    if (!user?.user_id) return;

    const loadLease = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await api.get(`/leases/user/${user.user_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setLease(res.data.lease || res.data);
      } catch (err) {
        const msg = err.response?.data?.message;
        if (msg === "Token expired") handleLogout();
      }
    };
    loadLease();
  }, [user, handleLogout]);

  // Load passcodes
  useEffect(() => {
    if (!lease?.lease_id) return;

    api
      .get(`/smart-passcodes/${lease.lease_id}`)
      .then((res) => setPasscodes(res.data))
      .catch((err) => {
        const msg = err.response?.data?.message;
        if (msg === "Token expired") handleLogout();
      });
  }, [lease, handleLogout]);

  const handlePaymentClick = async () => {
    try {
      const res = await api.post(
        "/payments/create-session",
        {
          amount: lease?.rent_amount || 1000,
          lease_id: lease?.lease_id,
          user_id: user?.user_id,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      if (res.data?.url) window.location.href = res.data.url;
    } catch (err) {
      const msg = err.response?.data?.message;
      if (msg === "Token expired") handleLogout();
    }
  };

  return (
    <div style={styles.page}>
      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>
          <span style={styles.homeIcon}>üè†</span> Tenant Portal
        </div>
        <div style={styles.navRight}>
          {location.pathname !== "/tenant/profile" && (
            <button style={styles.navButton} onClick={() => navigate("/tenant/profile")}>
              Profile
            </button>
          )}
          {location.pathname !== "/tenant" && (
            <button style={styles.navButton} onClick={() => navigate("/tenant")}>
              Dashboard
            </button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>
            Logout ‚Üí
          </button>
        </div>
      </nav>

      {/* SUMMARY CARDS */}
      {lease && (
        <div style={styles.summaryBar}>
          <div style={styles.summaryCard}>
            <div style={styles.summaryIcon}>üíµ</div>
            <p style={styles.summaryTitle}>Next Rent Due</p>
            <p style={styles.summaryValue}>
              ${parseFloat(lease.rent_amount || 0).toFixed(2)}
            </p>
          </div>

          <div style={styles.summaryCard}>
            <div style={styles.summaryIcon}>üìÖ</div>
            <p style={styles.summaryTitle}>Lease Ends</p>
            <p style={styles.summaryValue}>
              {lease.end_date ? new Date(lease.end_date).toLocaleDateString() : "‚Äî"}
            </p>
          </div>

          <div style={styles.summaryCard}>
            <div style={styles.summaryIcon}>üí≥</div>
            <p style={styles.summaryTitle}>Payments Made</p>
            <p style={styles.summaryValue}>$0.00</p>
          </div>
        </div>
      )}

      {/* MAIN GRID */}
      <div style={styles.grid}>
        {/* Heading */}
        <h1 style={styles.heading}>Welcome, {user?.name}</h1>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* Tenant Profile */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üë§ Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
        </div>

        {/* Payment History */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üí≥ Payment History</h3>
          <TenantPayments userId={user?.user_id} />
          <button style={styles.payButton} onClick={handlePaymentClick}>
            Make a Payment
          </button>
        </div>

        {/* Lease Info */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üìÑ Lease Information</h3>
          <TenantLeaseCard lease={lease} user={user} showTitle={false} />
        </div>

        {/* Lease Photos */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üì∑ Lease Photos</h3>
          <LeasePhotosForm
            leaseId={lease?.lease_id}
            token={localStorage.getItem("token")}
            role={user?.role}
            showTitle={false}
          />
        </div>

        {/* Documents */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üìÅ Documents</h3>
          <DocumentsForm
            leaseId={lease?.lease_id}
            userId={user?.user_id}
            role={user?.role}
            showTitle={false}
          />
        </div>

        {/* Reminders */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üîî Reminders</h3>
          {reminders.length === 0 ? (
            <p style={styles.emptyText}>No reminders yet üéâ</p>
          ) : (
            <ul style={styles.list}>
              {reminders.map((r) => (
                <li key={r.reminder_id} style={styles.listItem}>
                  <strong>{r.type}:</strong> {r.message}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Smart Passcodes */}
        <div style={styles.card}>
          <h3 style={styles.cardTitle}>üîê Smart Passcodes</h3>
          {passcodes.length === 0 ? (
            <p style={styles.emptyText}>No active passcodes</p>
          ) : (
            <ul style={styles.list}>
              {passcodes.map((pc) => (
                <li key={pc.passcode_id} style={styles.listItem}>
                  Code: {pc.passcode}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: "#F3F6FB",
    minHeight: "100vh",
  },

  /* NAVBAR */
  navbar: {
    background: "#0F172A",
    color: "#fff",
    padding: "1rem 2rem",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  navLeft: { fontSize: "1.3rem", fontWeight: 600, display: "flex", gap: ".4rem" },
  navButton: {
    background: "transparent",
    border: "1px solid #fff",
    padding: ".45rem 1rem",
    color: "#fff",
    borderRadius: "8px",
    cursor: "pointer",
  },
  logoutButton: {
    background: "#DC2626",
    color: "#fff",
    padding: ".45rem 1rem",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    fontWeight: 600,
  },
  navRight: { display: "flex", gap: "1rem" },

  /* SUMMARY CARDS */
  summaryBar: {
    display: "flex",
    justifyContent: "center",
    gap: "1.5rem",
    margin: "2rem auto",
    padding: "0 1rem",
    maxWidth: "1000px",
  },
  summaryCard: {
    background: "#fff",
    borderRadius: "14px",
    border: "1px solid #E5E7EB",
    padding: "1.3rem 2rem",
    minWidth: "200px",
    textAlign: "center",
  },
  summaryIcon: { fontSize: "2rem" },
  summaryTitle: { marginTop: ".5rem", color: "#4B5563", fontWeight: 600 },
  summaryValue: { fontSize: "1.5rem", fontWeight: 700, marginTop: ".2rem" },

  /* GRID LAYOUT */
  grid: {
    padding: "2rem",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "2rem",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  heading: {
    gridColumn: "1 / -1",
    textAlign: "center",
    fontSize: "2rem",
    fontWeight: 700,
  },
  role: {
    gridColumn: "1 / -1",
    textAlign: "center",
    color: "#6B7280",
    marginBottom: "1rem",
  },

  /* CARDS */
  card: {
    background: "#fff",
    borderRadius: "14px",
    border: "1px solid #E5E7EB",
    padding: "1.7rem",
  },
  cardTitle: {
    fontSize: "1.2rem",
    fontWeight: 700,
    marginBottom: "1rem",
    borderBottom: "1px solid #E5E7EB",
    paddingBottom: ".7rem",
  },

  payButton: {
    marginTop: "1rem",
    background: "#1e3a8a",
    color: "#fff",
    padding: ".7rem 1.3rem",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    fontWeight: 600,
  },

  emptyText: { color: "#6B7280", fontStyle: "italic" },

  list: { padding: 0, margin: 0, listStyle: "none" },
  listItem: { padding: ".5rem 0", borderBottom: "1px solid #E5E7EB" },
};

export default TenantDashboard;
