import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LeaseForm from '../components/LeaseForm';
import LeaseList from '../components/LeaseList';

axios.defaults.baseURL = 'http://localhost:5000';

const AdminLeases = ({ user }) => {
  const [leases, setLeases] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchLeases = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return alert('Missing auth token');

      const res = await axios.get('/api/leases', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setLeases(res.data);
    } catch (err) {
      console.error('âŒ Fetch leases error:', err);
      setError('Failed to load leases');
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async () => {
    await fetchLeases(); // refresh after creation
  };

  const handleEdit = async (updatedLease) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`/api/leases/${updatedLease.lease_id}`, updatedLease, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setLeases(prev =>
        prev.map(l => (l.lease_id === updatedLease.lease_id ? updatedLease : l))
      );
      alert(`âœ… Lease ${updatedLease.lease_id} updated successfully`);
    } catch (err) {
      console.error('âŒ Update lease error:', err);
      alert('Failed to update lease');
    }
  };

  const handleDelete = async (leaseId) => {
    const confirmDelete = window.confirm(`Are you sure you want to delete lease #${leaseId}?`);
    if (!confirmDelete) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/leases/${leaseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setLeases(prev => prev.filter(l => l.lease_id !== leaseId));
      alert(`âœ… Lease ${leaseId} deleted successfully`);
    } catch (err) {
      console.error('âŒ Delete lease error:', err);
      alert(err.response?.data?.error || 'Failed to delete lease');
    }
  };

  useEffect(() => {
  fetchLeases();
}, []);

if (!user || !['admin', 'landlord'].includes(user.role.toLowerCase())) {
  return <p style={{ color: '#c00', textAlign: 'center' }}>ðŸš« Unauthorized access</p>;
}

if (loading) return <p style={{ textAlign: 'center' }}>Loading leases...</p>;
if (error) return <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>;

return (
  <div style={{ padding: '2rem' }}>
    <div style={{ marginBottom: '1rem' }}>
      <h2>Welcome, {user.name}</h2>
      <p>Role: {user.role}</p>
    </div>

    {/* âœ… Pass user down */}
    <LeaseForm onCreate={handleCreate} user={user} />

    <LeaseList
      leases={leases}
      onEdit={handleEdit}
      onDelete={handleDelete}
      user={user}
    />
  </div>
);
};

export default AdminLeases;
