import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ user, onLeaseLoaded }) => {
  const [lease, setLease] = useState(null);

  useEffect(() => {
    const fetchLease = async () => {
      try {
        console.log('üß© TenantLeaseCard mounted for user:', user.id);

        const token = localStorage.getItem('token');
        const url = `/api/leases/user/${user.id}`;
        console.log('üì° Lease API call URL:', url);

        const res = await axios.get(url, {
          headers: { Authorization: `Bearer ${token}` }
        });

        console.log('üì¶ Lease API response:', res.data);

        const userLease = res.data || null;
        setLease(userLease);

        // ‚úÖ Always pass lease up, even if lean or null
        if (typeof onLeaseLoaded === 'function') {
          console.log('üì§ Passing lease up to dashboard:', userLease);
          onLeaseLoaded(userLease);
        }
      } catch (err) {
        console.error('‚ùå Lease fetch error:', err.response?.data || err.message);
        setLease(null);
        if (typeof onLeaseLoaded === 'function') {
          console.log('üì§ Passing null lease up due to error');
          onLeaseLoaded(null);
        }
      }
    };

    fetchLease();
  }, [user, onLeaseLoaded]);

  return (
    <div style={cardStyle}>
      {lease ? (
        <>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Property ID:</strong> {lease.property_id}</p>
          {lease.rent_amount !== undefined && (
            <p><strong>Rent:</strong> ${lease.rent_amount}</p>
          )}
          <p><strong>Start:</strong> {new Date(lease.start_date).toLocaleDateString()}</p>
          <p><strong>End:</strong> {new Date(lease.end_date).toLocaleDateString()}</p>
          <p><strong>Status:</strong> {lease.renewal_requested ? 'Renewal requested' : 'Active'}</p>
          {lease.lease_file_url && (
            <p><strong>Lease File:</strong> {lease.lease_file_url}</p>
          )}
        </>
      ) : (
        <p>No lease found.</p>
      )}
    </div>
  );
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff',
  marginBottom: '2rem'
};

export default TenantLeaseCard;
