import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function TenantLeaseCard({ userId }) {
  const [lease, setLease] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchLease = async () => {
      console.log('üì® userId passed to TenantLeaseCard:', userId);
      try {
        const res = await api.get(`/leases/user/${userId}`);
        setLease(res.data);
      } catch (err) {
        console.error('‚ùå Lease fetch error:', err);
        setError(true);
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchLease();
    }
  }, [userId]);

  if (loading) return <p>Loading lease info...</p>;
  if (error || !lease) return <p>Lease not found.</p>;

  return (
    <div>
      <h3>Lease Details</h3>
      <p><strong>Address:</strong> {lease?.address}, {lease?.city}, {lease?.state} {lease?.zip}</p>
      <p><strong>Start Date:</strong> {new Date(lease?.start_date).toLocaleDateString()}</p>
      <p><strong>End Date:</strong> {new Date(lease?.end_date).toLocaleDateString()}</p>
      <p><strong>Rent:</strong> ${parseFloat(lease?.rent_amount).toFixed(2)}</p>
      <p><strong>Renewal Requested:</strong> {lease?.renewal_requested ? 'Yes' : 'No'}</p>
    </div>
  );
}
