import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ userId }) => {
  const [lease, setLease] = useState(null);

  useEffect(() => {
    const fetchLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/leases', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const userLease = res.data.find(l => l.tenant_id === userId);
        setLease(userLease || null);
      } catch (err) {
        console.error('Lease fetch error:', err);
      }
    };
    fetchLease();
  }, [userId]);

  return (
    <div style={cardStyle}>
      {lease ? (
        <div style={infoContainer}>
          <InfoRow label="Property ID" value={lease.property_id} />
          <InfoRow label="Rent" value={lease.rent_amount > 0 ? `$${lease.rent_amount}` : 'Not set'} />
          <InfoRow label="Start" value={new Date(lease.start_date).toLocaleDateString()} />
          <InfoRow label="End" value={new Date(lease.end_date).toLocaleDateString()} />
          <InfoRow label="Status" value={lease.status || 'N/A'} />
        </div>
      ) : (
        <p>No lease found.</p>
      )}
    </div>
  );
};

const InfoRow = ({ label, value }) => (
  <p style={rowStyle}>
    <span style={labelStyle}>{label}:</span> <span style={valueStyle}>{value}</span>
  </p>
);

const cardStyle = {
  backgroundColor: '#fff',
  borderRadius: '8px',
  boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
  padding: '1.5rem',
  width: '100%',
  maxWidth: '500px',
};

const infoContainer = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.75rem',
};

const rowStyle = {
  margin: 0,
  fontSize: '1rem',
};

const labelStyle = {
  fontWeight: 'bold',
  color: '#444',
};

const valueStyle = {
  color: '#222',
};

export default TenantLeaseCard;
