import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ userId }) => {
  const [lease, setLease] = useState(null);

  useEffect(() => {
    const fetchLease = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/leases', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const userLease = res.data.find(l => l.tenant_id === userId);
        setLease(userLease || null);
      } catch (err) {
        console.error('Lease fetch error:', err);
      }
    };
    fetchLease();
  }, [userId]);

  return (
    <div style={cardStyle}>
      {lease ? (
        <>
          <p style={rowStyle}><span style={labelStyle}>Property ID:</span> {lease.property_id}</p>
          <p style={rowStyle}><span style={labelStyle}>Rent:</span> {lease.rent_amount > 0 ? `$${lease.rent_amount}` : 'Not set'}</p>
          <p style={rowStyle}><span style={labelStyle}>Start:</span> {new Date(lease.start_date).toLocaleDateString()}</p>
          <p style={rowStyle}><span style={labelStyle}>End:</span> {new Date(lease.end_date).toLocaleDateString()}</p>
          <p style={rowStyle}><span style={labelStyle}>Status:</span> {lease.status || 'N/A'}</p>
        </>
      ) : (
        <p>No lease found.</p>
      )}
    </div>
  );
};

const cardStyle = {
  backgroundColor: '#fefefe',
  borderRadius: '8px',
  boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
  padding: '1.5rem',
  width: '100%',
  maxWidth: '500px',
};

const rowStyle = {
  marginBottom: '0.75rem',
  fontSize: '1rem',
  color: '#333',
};

const labelStyle = {
  fontWeight: 'bold',
  marginRight: '0.5rem',
  color: '#555',
};

export default TenantLeaseCard;
