import React, { useState } from 'react';
import axios from 'axios';

const PropertyForm = ({ onCreate }) => {
  const [formData, setFormData] = useState({
    address: '',
    city: '',
    state: '',
    zip: '',
    rent_amount: '',
    status: 'available',
    user_id: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/properties', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreate(res.data); // pass new property to parent
      setFormData({
        address: '',
        city: '',
        state: '',
        zip: '',
        rent_amount: '',
        status: 'available',
        user_id: ''
      });
    } catch (err) {
      console.error('Create error:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h3>Add New Property</h3>
      <input name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <input name="city" placeholder="City" value={formData.city} onChange={handleChange} required />
      <input name="state" placeholder="State" value={formData.state} onChange={handleChange} required />
      <input name="zip" placeholder="ZIP" value={formData.zip} onChange={handleChange} required />
      <input name="rent_amount" placeholder="Rent Amount" type="number" value={formData.rent_amount} onChange={handleChange} required />
      <select name="status" value={formData.status} onChange={handleChange}>
        <option value="available">Available</option>
        <option value="occupied">Occupied</option>
      </select>
      <input name="user_id" placeholder="Owner User ID" value={formData.user_id} onChange={handleChange} required />
      <button type="submit">Create Property</button>
    </form>
  );
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem',
  marginBottom: '2rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

export default PropertyForm;
