import React, { useState } from 'react';
import axios from 'axios';

axios.defaults.baseURL = 'http://localhost:5000';

const MaintenanceForm = ({ onCreate, user }) => {
  const [formData, setFormData] = useState({
    property_id: '',
    description: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/maintenance', formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      // Pass new request back to parent (MaintenancePage)
      onCreate(res.data.request);

      setMessage('✅ Maintenance request submitted successfully');
      setFormData({ property_id: '', description: '' });
    } catch (err) {
      console.error('❌ Submit error:', err);
      setMessage('❌ Failed to submit request');
    }
  };

  if (!user || user.role !== 'tenant') return null;

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <h3>Submit Maintenance Request</h3>
      <input
        name="property_id"
        placeholder="Property ID"
        value={formData.property_id}
        onChange={handleChange}
        required
      />
      <textarea
        name="description"
        placeholder="Describe the issue"
        value={formData.description}
        onChange={handleChange}
        rows={3}
        required
      />
      <button type="submit" style={submitButton}>Submit</button>
      {message && (
        <p style={{ color: message.startsWith('✅') ? 'green' : 'red' }}>
          {message}
        </p>
      )}
    </form>
  );
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '0.5rem',
  marginBottom: '2rem',
  padding: '1rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  backgroundColor: '#fefefe'
};

const submitButton = {
  padding: '0.5rem 1rem',
  backgroundColor: '#0078D4',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease'
};

export default MaintenanceForm;
