import React, { useEffect, useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '../services/api';

export default function LandlordPayments() {
  const navigate = useNavigate();

  // âœ… Stabilize token and decode user info
  const token = useMemo(() => localStorage.getItem('token'), []);
  const decoded = useMemo(() => (token ? jwtDecode(token) : null), [token]);
  const landlordId = useMemo(() => decoded?.user_id, [decoded]);

  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!decoded || decoded.role?.toLowerCase() !== 'landlord') {
      navigate('/unauthorized');
      return;
    }

    if (!landlordId) {
      console.error('âŒ Missing landlord ID');
      return;
    }

    api
      .get(`/payments/landlord/${landlordId}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => {
        console.log('âœ… Payments response:', res.data);
        setPayments(res.data);
      })
      .catch(err =>
        console.error(
          'âŒ Error loading landlord payments:',
          err.response?.data || err.message
        )
      )
      .finally(() => setLoading(false));
  }, [landlordId, decoded, token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (loading) {
    return <p style={loadingStyle}>Loading payments...</p>;
  }

  return (
    <div style={pageStyle}>
      <nav style={navStyle}>
        <div style={navLeft}>ðŸ’³ Landlord Portal â€” Payments</div>
        <div style={navRight}>
          <button style={navButton} onClick={() => navigate('/landlord')}>
            Dashboard
          </button>
          <button style={logoutButton} onClick={handleLogout}>
            Logout
          </button>
        </div>
      </nav>

      <div style={containerStyle}>
        <h2 style={headingStyle}>My Payments</h2>

        {Array.isArray(payments) && payments.length > 0 ? (
          <div style={cardGrid}>
            {payments.map(pay => (
              <div key={pay.payment_id} style={card}>
                <h3 style={cardTitle}>
  {pay.tenant_name} â€” ${pay.total_paid}
</h3>
<p>
  Property: {pay.property_address}, {pay.city}, {pay.state}
</p>
<p>Last Paid: {new Date(pay.last_paid_date).toLocaleDateString()}</p>
<p>
  Rent Cycle:{' '}
  {new Date(pay.payment_for_month).toLocaleDateString('en-US', {
    month: 'long',
    year: 'numeric'
  })}
</p>
<p>Status: {pay.latest_status}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No payments found.</p>
        )}
      </div>
    </div>
  );
}

/* Styles */
const pageStyle = {
  background: '#f9fafb',
  minHeight: '100vh',
  fontFamily: 'Segoe UI, sans-serif'
};
const navStyle = {
  background: '#2563eb',
  color: '#fff',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '1rem 2rem',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  position: 'sticky',
  top: 0,
  zIndex: 1000,
  width: '100%'
};
const navLeft = { fontWeight: 'bold', fontSize: '1.5rem' };
const navRight = { display: 'flex', gap: '1rem' };
const navButton = {
  background: 'transparent',
  border: '1px solid #fff',
  color: '#fff',
  fontSize: '1rem',
  fontWeight: '500',
  cursor: 'pointer',
  padding: '0.5rem 1rem',
  borderRadius: '6px'
};
const logoutButton = {
  background: '#dc2626',
  border: 'none',
  color: '#fff',
  padding: '0.5rem 1rem',
  borderRadius: '6px',
  fontWeight: '500',
  cursor: 'pointer'
};
const containerStyle = {
  padding: '2rem',
  width: '100%',
  maxWidth: '1200px',
  margin: '0 auto'
};
const headingStyle = { fontSize: '2rem', color: '#111', marginBottom: '1.5rem' };
const loadingStyle = {
  textAlign: 'center',
  fontSize: '1.2rem',
  marginTop: '3rem',
  color: '#555'
};
const cardGrid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
  gap: '1.5rem'
};
const card = {
  backgroundColor: '#fff',
  borderRadius: '12px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
  padding: '1.5rem'
};
const cardTitle = {
  fontSize: '1.25rem',
  fontWeight: '600',
  marginBottom: '0.5rem',
  color: '#111'
};
