// src/components/TenantPayments.jsx

import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function TenantPayments({ userId }) {
  const [payments, setPayments] = useState(null);

  useEffect(() => {
  if (!userId) return;

const token = localStorage.getItem('token');
api.get('/payments/user', {
  headers: { Authorization: `Bearer ${token}` }
})
    .then(res => setPayments(res.data))
    .catch(() => setPayments([]));
}, [userId]);

  if (!payments) return <p>Loading payment history...</p>;
  if (payments.length === 0) return <p>No payments found.</p>;

  return (
    <div>
     <table style={tableStyle}>
  <thead>
    <tr>
      <th style={thTdStyle}>Date</th>
      <th style={thTdStyle}>Amount</th>
      <th style={thTdStyle}>Status</th>
      <th style={thTdStyle}>Late Fee</th>
      <th style={thTdStyle}>Note</th>
    </tr>
  </thead>
  <tbody>
    {payments.map((payment) => (
      <tr key={payment.payment_id}>
        <td style={thTdStyle}>{new Date(payment.paid_date).toLocaleDateString()}</td>
        <td style={thTdStyle}>${parseFloat(payment.amount).toLocaleString()}</td>
        <td style={thTdStyle}>{payment.status}</td>
        <td style={thTdStyle}>{payment.late_fee_applied ? 'Yes' : 'No'}</td>
        <td style={thTdStyle}>{payment.note || '—'}</td>
      </tr>
    ))}
  </tbody>
</table>
    </div>
  );
}
const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: '1rem',
};

const thTdStyle = {
  border: '1px solid #ccc',
  padding: '0.75rem',
  textAlign: 'left',
};
