import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const LeasePhotosForm = ({ leaseId, token, role }) => {
  const [photos, setPhotos] = useState([]);
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchPhotos = useCallback(async () => {
    try {
      const res = await axios.get(`/api/lease-photos/${leaseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPhotos(res.data);
    } catch (err) {
      console.error('Error fetching photos:', err);
    }
  }, [leaseId, token]);

  useEffect(() => {
    fetchPhotos();
  }, [fetchPhotos]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      setLoading(true);
      await axios.post(`/api/lease-photos/upload/${leaseId}`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setFile(null);
      fetchPhotos();
    } catch (err) {
      console.error('Upload error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (photoId) => {
    try {
      await axios.delete(`/api/lease-photos/${photoId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchPhotos();
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  const handleToggleVisibility = async (photoId) => {
    try {
      await axios.patch(`/api/lease-photos/visibility/${photoId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchPhotos();
    } catch (err) {
      console.error('Visibility toggle error:', err);
    }
  };

  return (
    <div style={styles.card}>
      <h3 style={styles.sectionTitle}>Lease Photos</h3>

      {role !== 'tenant' && (
        <form onSubmit={handleUpload} style={styles.form}>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setFile(e.target.files[0])}
          />
          <button type="submit" disabled={loading || !file}>
            {loading ? 'Uploading...' : 'Upload Photo'}
          </button>
        </form>
      )}

      {photos.length === 0 ? (
        <p style={styles.empty}>ðŸ“· No lease photos available.</p>
      ) : (
        <div style={styles.photoGrid}>
          {photos
            .filter(p => role !== 'tenant' || p.is_visible_to_tenant)
            .map((p) => (
              <figure key={p.id} style={styles.photoWrapper}>
                <img
  src={`/files/${p.url}`}
  alt={`Uploaded ${p.uploaded_at ? new Date(p.uploaded_at).toLocaleDateString() : 'N/A'}`}
  style={styles.photo}
/>
                <figcaption style={styles.caption}>
                  Uploaded: {p.uploaded_at ? new Date(p.uploaded_at).toLocaleDateString() : 'N/A'}
                </figcaption>

                {role !== 'tenant' && (
                  <div style={styles.controls}>
                    <label>
                      <input
                        type="checkbox"
                        checked={p.is_visible_to_tenant}
                        onChange={() => handleToggleVisibility(p.id)}
                      />
                      Visible to Tenant
                    </label>
                    <button onClick={() => handleDelete(p.id)} style={styles.deleteButton}>
                      Delete
                    </button>
                  </div>
                )}
              </figure>
            ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  card: { padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' },
  sectionTitle: { marginBottom: '1rem' },
  form: { marginBottom: '1rem' },
  empty: { color: '#888', fontStyle: 'italic', textAlign: 'center' },
  photoGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
    gap: '1rem'
  },
  photoWrapper: { textAlign: 'center' },
  photo: {
    width: '150px',
    height: '100px',
    objectFit: 'cover',
    borderRadius: '8px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
  },
  caption: { fontSize: '0.8rem', color: '#666', marginTop: '0.25rem' },
  controls: { marginTop: '0.5rem', fontSize: '0.85rem', color: '#444' },
  deleteButton: {
    marginLeft: '0.5rem',
    background: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '0.25rem 0.5rem',
    borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default LeasePhotosForm;
