import React from 'react';

const SmartPasscode = ({ passcode, onRevoke, onDelete }) => {
  const { passcode_id, passcode: code, is_active, expires_at } = passcode;

  // Always show passcode as 4-digit string (preserve leading zeros)
  const displayCode = code ? code.toString().padStart(4, '0') : '----';

  const status = is_active ? 'Active' : 'Revoked';
  const badgeColor = is_active ? '#2ecc71' : '#e74c3c';

  const formattedExpiry = expires_at
    ? new Date(expires_at).toLocaleString()
    : 'No expiry set';

  return (
    <div style={card}>
      <div style={row}>
        <span style={codeText}>ðŸ”‘ {displayCode}</span>
        <span style={{ ...badge, backgroundColor: badgeColor }}>{status}</span>
      </div>

      <div style={detail}>Expires: {formattedExpiry}</div>

      <div style={actions}>
        {is_active && (
          <button
            style={revokeBtn}
            onClick={() => onRevoke(passcode_id)}
            aria-label="Revoke passcode"
          >
            Revoke
          </button>
        )}
        <button
          style={deleteBtn}
          onClick={() => onDelete(passcode_id)}
          aria-label="Delete passcode"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

/* ðŸŽ¨ Styles */
const card = {
  background: '#fff',
  padding: '1rem',
  borderRadius: '12px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
  display: 'flex',
  flexDirection: 'column',
  gap: '0.75rem',
  transition: 'box-shadow 0.2s ease',
};
const row = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
};
const codeText = {
  fontSize: '1.2rem',
  fontWeight: '600',
};
const badge = {
  padding: '0.3rem 0.6rem',
  borderRadius: '6px',
  color: '#fff',
  fontSize: '0.85rem',
  fontWeight: '500',
  boxShadow: '0 1px 4px rgba(0,0,0,0.1)',
};
const detail = {
  fontSize: '0.95rem',
  color: '#555',
};
const actions = {
  display: 'flex',
  gap: '0.5rem',
};
const baseBtn = {
  padding: '0.4rem 0.8rem',
  border: 'none',
  borderRadius: '6px',
  color: '#fff',
  fontWeight: '500',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease',
};
const revokeBtn = {
  ...baseBtn,
  backgroundColor: '#f39c12',
};
const deleteBtn = {
  ...baseBtn,
  backgroundColor: '#e74c3c',
};

export default SmartPasscode;
