import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function LandlordDashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || user.role?.toLowerCase() !== 'landlord') {
      navigate('/unauthorized');
      return;
    }

    const landlordId = user.id || user.user_id;   // ‚úÖ safe fallback

    Promise.all([
      api.get('/api/properties/landlord/all', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      }),
      api.get(`/leases/landlord-tenants/${landlordId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      }),
      api.get(`/payments/landlord/${landlordId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      }),
      api.get(`/maintenance/landlord/${landlordId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      })
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        console.log("üì¶ Dashboard properties response:", propsRes.data);
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord dashboard:", err.response?.data || err.message)
      )
      .finally(() => setLoading(false));
  }, [user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  // Quick Stats
  const totalProperties = properties.length;
  const ownedProperties = properties.filter(p => p.is_owned === 1).length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);
  const openRequests = maintenance.filter(m => m.status !== 'completed').length;

  return (
    <div style={styles.page}>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/properties')}>Properties</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/tenants')}>Tenants</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/payments')}>Payments</button>
          <button style={styles.navButton} onClick={() => navigate('/landlord/maintenance')}>Maintenance</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* Quick Stats Bar */}
        <div style={styles.statsBar}>
          <div style={styles.stat}>
            <strong>üè† Properties:</strong> {loading ? '...' : totalProperties}
            <span style={{ color: '#2563eb', fontWeight: '500' }}>
              {loading ? '' : ` (Owned: ${ownedProperties})`}
            </span>
          </div>
          <div style={styles.stat}><strong>üë• Tenants:</strong> {loading ? '...' : totalTenants}</div>
          <div style={styles.stat}><strong>üí≥ Rent Collected:</strong> {loading ? '...' : `$${totalRentCollected.toFixed(2)}`}</div>
          <div style={styles.stat}><strong>üõ†Ô∏è Open Requests:</strong> {loading ? '...' : openRequests}</div>
        </div>

        {/* Grid of Cards */}
        <div style={styles.grid}>
          {/* Properties */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üè† Properties</h3>
            {loading ? (
              <p>Loading properties...</p>
            ) : properties.length === 0 ? (
              <p>No properties found in the system.</p>
            ) : (
              <ul style={styles.list}>
                {properties.slice(0, 3).map(p => (
                  <li key={p.property_id}>
                    <strong>{p.address}</strong> ‚Äî {p.city}, {p.state}
                    <div>Status: {p.status}</div>
                    <div>Rent: ${p.rent_amount}</div>
                    {p.is_owned === 1 && (
                      <div style={{ color: '#2563eb', fontWeight: 'bold' }}>‚úÖ Your Property</div>
                    )}
                  </li>
                ))}
              </ul>
            )}
            <button style={styles.viewAllButton} onClick={() => navigate('/landlord/properties')}>
              View All Properties
            </button>
          </div>

          {/* Tenants */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üë• Tenants</h3>
            {loading ? (
              <p>Loading tenants...</p>
            ) : tenants.length === 0 ? (
              <p>No tenants assigned.</p>
            ) : (
              <ul style={styles.list}>
                {tenants.slice(0, 3).map(t => (
                  <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                ))}
              </ul>
            )}
            <button style={styles.viewAllButton} onClick={() => navigate('/landlord/tenants')}>
              View All Tenants
            </button>
          </div>

          {/* Payments */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üí≥ Payments</h3>
            {loading ? (
              <p>Loading payments...</p>
            ) : payments.length === 0 ? (
              <p>No payments recorded.</p>
            ) : (
              <ul style={styles.list}>
                {payments.slice(0, 3).map(pay => (
                  <li key={pay.payment_id}>
                    {pay.tenant_name} ‚Äî ${pay.amount} on {new Date(pay.date).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
            <button style={styles.viewAllButton} onClick={() => navigate('/landlord/payments')}>
              View All Payments
            </button>
          </div>

          {/* Maintenance */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üõ†Ô∏è Maintenance Requests</h3>
            {loading ? (
              <p>Loading requests...</p>
            ) : maintenance.length === 0 ? (
              <p>No maintenance requests.</p>
            ) : (
              <ul style={styles.list}>
                {maintenance.slice(0, 3).map(m => (
                  <li key={m.request_id}>
                    {m.description} ‚Äî {m.status} ({new Date(m.created_at).toLocaleDateString()})
                  </li>
                ))}
              </ul>
            )}
            <button style={styles.viewAllButton} onClick={() => navigate('/landlord/maintenance')}>
              View All Requests
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}



