import React from 'react';

const TenantLeaseCard = ({ lease, user }) => {
  if (!lease) return <p>Loading lease...</p>;

  return (
    <div style={cardStyle}>
      <p><strong>Name:</strong> {user?.name || 'N/A'}</p>
      <p><strong>Email:</strong> {user?.email || 'N/A'}</p>

      <p><strong>Lease ID:</strong> {lease.lease_id || 'N/A'}</p>
      <p><strong>Property ID:</strong> {lease.property_id || 'N/A'}</p>

      <p><strong>Rent:</strong> 
        {parseFloat(lease.rent_amount) > 0
          ? `$${parseFloat(lease.rent_amount).toFixed(2)}`
          : 'Not set'}
      </p>

      <p><strong>Start:</strong> {lease.start_date ? new Date(lease.start_date).toLocaleDateString() : 'N/A'}</p>
      <p><strong>End:</strong> {lease.end_date ? new Date(lease.end_date).toLocaleDateString() : 'N/A'}</p>

      <p>
        <strong>Status:</strong>{' '}
        {lease.renewal_requested === 1
          ? <span style={badgeRenewal}>Renewal requested</span>
          : <span style={badgeActive}>Active</span>}
      </p>

      <p>
        <strong>Lease File:</strong>{' '}
        {lease.lease_file_url
          ? <a
              href={`http://localhost:5000/uploads/${lease.lease_file_url}`}
              target="_blank"
              rel="noreferrer"
            >
              View File
            </a>
          : 'Not uploaded'}
      </p>
    </div>
  );
};

const cardStyle = {
  backgroundColor: '#ffffff',
  border: '1px solid #d9d9d9',
  borderRadius: '12px',
  padding: '1.75rem',
  marginBottom: '2rem',
  boxShadow: '0 4px 14px rgba(0, 0, 0, 0.08)',
  transition: 'transform 0.2s ease, box-shadow 0.2s ease',
};

const badgeBase = {
  padding: '0.35rem 0.75rem',
  borderRadius: '8px',
  fontSize: '0.85rem',
  fontWeight: 600,
  display: 'inline-block',
  boxShadow: '0 1px 3px rgba(0,0,0,0.12)',
};

const badgeActive = {
  ...badgeBase,
  backgroundColor: '#22c55e', // softer green
  color: '#ffffff',
};

const badgeRenewal = {
  ...badgeBase,
  backgroundColor: '#ffcd38', // smoother yellow
  color: '#212529',
};


export default TenantLeaseCard;
