import React, { useState } from 'react';
import axios from 'axios';

const PasscodesPage = ({ leases = [], users = [] }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [passcodes, setPasscodes] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const lease_id = Number(leaseId);
    const user_id = Number(userId);
    const expires_at = expiresAt ? new Date(expiresAt).toISOString() : null;

    if (!lease_id || !user_id || isNaN(lease_id) || isNaN(user_id)) {
      setError('Please select valid Lease and User.');
      return;
    }

    setSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        '/api/smart-passcodes',
        { lease_id, user_id, expires_at },
        token ? { headers: { Authorization: `Bearer ${token}` } } : undefined
      );

      setPasscodes([...passcodes, res.data.passcode]);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
      setSuccess('âœ… Passcode generated successfully!');
    } catch (err) {
      console.error('âŒ Create error:', err);
      setError('Failed to generate passcode. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleRevoke = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/smart-passcodes/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : undefined
      });
      setPasscodes(passcodes.filter(p => p.passcode_id !== id));
    } catch (err) {
      console.error('âŒ Revoke error:', err);
      alert('Failed to revoke passcode.');
    }
  };

  const filteredUsers = leaseId
    ? users.filter(u => String(u.lease_id) === leaseId)
    : [];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Smart Passcodes</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} style={form}>
        <h3 style={formTitle}>Generate Smart Passcode</h3>

        <div style={row}>
          <div style={field}>
            <label style={label}>Property</label>
            <select
              value={leaseId}
              onChange={(e) => {
                setLeaseId(e.target.value);
                setUserId('');
              }}
              style={select}
            >
              <option value="" disabled>Select Property</option>
              {(leases || []).map((l) => (
                <option key={l.lease_id} value={String(l.lease_id)}>
                  Lease #{l.lease_id} â€“ Property {l.property_id}
                </option>
              ))}
            </select>
          </div>

          <div style={field}>
            <label style={label}>User</label>
            <select
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              style={select}
              disabled={!leaseId}
            >
              <option value="" disabled>
                {leaseId ? 'Select User' : 'Choose property first'}
              </option>
              {(filteredUsers || []).map((u) => (
                <option key={u.user_id} value={String(u.user_id)}>
                  {u.name || `User #${u.user_id}`}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div style={field}>
          <label style={label}>Expiry (optional)</label>
          <input
            type="datetime-local"
            value={expiresAt}
            onChange={(e) => setExpiresAt(e.target.value)}
            style={input}
          />
        </div>

        <button
          type="submit"
          style={{
            ...button,
            opacity: submitting ? 0.7 : 1,
            cursor: submitting ? 'not-allowed' : 'pointer'
          }}
          disabled={submitting}
        >
          {submitting ? 'Generatingâ€¦' : 'Generate Passcode'}
        </button>

        {error && <div style={errorMsg}>{error}</div>}
        {success && <div style={successMsg}>{success}</div>}
      </form>

      {/* Active Passcodes Table */}
      <div style={{ marginTop: '2rem' }}>
        <h3>Active Passcodes</h3>
        {passcodes.length === 0 ? (
          <p>No active passcodes yet. Generate one above to get started.</p>
        ) : (
          <table style={table}>
            <thead>
              <tr>
                <th style={th}>Code</th>
                <th style={th}>Lease</th>
                <th style={th}>User</th>
                <th style={th}>Expiry</th>
                <th style={th}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {passcodes.map((p) => (
                <tr key={p.passcode_id}>
                  <td style={td}>{p.passcode}</td>
                  <td style={td}>Lease #{p.lease_id}</td>
                  <td style={td}>User #{p.user_id}</td>
                  <td style={td}>
                    {p.expires_at ? new Date(p.expires_at).toLocaleString() : 'No expiry'}
                  </td>
                  <td style={td}>
                    <button style={revokeBtn} onClick={() => handleRevoke(p.passcode_id)}>
                      Revoke
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

/* ðŸŽ¨ Styles */
const form = {
  background: '#fff',
  padding: '1.5rem',
  borderRadius: '12px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
  marginBottom: '2rem',
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  maxWidth: 600
};
const formTitle = { fontSize: '1.2rem', fontWeight: '600' };
const row = { display: 'flex', gap: '1rem', flexWrap: 'wrap' };
const field = { flex: 1, display: 'flex', flexDirection: 'column' };
const label = { marginBottom: '0.4rem', fontWeight: '500', fontSize: '0.9rem' };
const select = {
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem',
  backgroundColor: '#fff',
  cursor: 'pointer'
};
const input = {
  padding: '0.5rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '0.95rem'
};
const button = {
  alignSelf: 'flex-start',
  padding: '0.6rem 1.2rem',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease'
};
const errorMsg = { color: '#e74c3c', fontWeight: '500' };
const successMsg = { color: '#2ecc71', fontWeight: '500' };
const table = { width: '100%', borderCollapse: 'collapse', marginTop: '1rem' };
const th = { borderBottom: '2px solid #ccc', textAlign: 'left', padding: '0.5rem' };
const td = { borderBottom: '1px solid #eee', padding: '0.5rem' };
const revokeBtn = {
  padding: '0.4rem 0.8rem',
  border: 'none',
  borderRadius: '6px',
  backgroundColor: '#e74c3c',
  color: '#fff',
  fontWeight: '600',
  cursor: 'pointer'
};

export default PasscodesPage;
