import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MaintenanceForm from '../components/MaintenanceForm';
import MaintenanceList from '../components/MaintenanceList';

axios.defaults.baseURL = 'http://localhost:5000';

const MaintenancePage = ({ user }) => {
  const [requests, setRequests] = useState([]);
  const [filter, setFilter] = useState('all');
  const [search, setSearch] = useState('');

  const fetchRequests = async () => {
    try {
      const res = await axios.get('/api/maintenance', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(res.data);
    } catch (err) {
      console.error('‚ùå Fetch error:', err);
    }
  };

  const handleCreate = (newRequest) => {
    setRequests(prev => [...prev, newRequest]);
  };

  const handleUpdate = async (id, data) => {
    try {
      await axios.patch(`/api/maintenance/${id}`, data, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(prev =>
        prev.map(r => (r.request_id === id ? { ...r, ...data } : r))
      );
    } catch (err) {
      console.error('‚ùå Update error:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/maintenance/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRequests(prev => prev.filter(r => r.request_id !== id));
    } catch (err) {
      console.error('‚ùå Delete error:', err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  // Apply filter + search
  const filteredRequests = requests.filter(r => {
    const matchesFilter = filter === 'all' || r.status === filter;
    const matchesSearch =
      r.description.toLowerCase().includes(search.toLowerCase()) ||
      String(r.property_id).includes(search);
    return matchesFilter && matchesSearch;
  });

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üõ†Ô∏è Maintenance Requests</h2>

      {/* Filter + Search controls (admin/landlord only) */}
      {['admin','landlord'].includes(user?.role) && (
        <div style={filterRow}>
          <button onClick={() => setFilter('all')}>All</button>
          <button onClick={() => setFilter('open')}>Open</button>
          <button onClick={() => setFilter('in_progress')}>In Progress</button>
          <button onClick={() => setFilter('closed')}>Closed</button>
          <input
            type="text"
            placeholder="Search by description or property ID"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={searchBox}
          />
        </div>
      )}

      {/* Tenant form */}
      <MaintenanceForm onCreate={handleCreate} user={user} />

      {/* Admin/Landlord list */}
      <MaintenanceList
        requests={filteredRequests}
        onUpdate={handleUpdate}
        onDelete={handleDelete}
        user={user}
      />
    </div>
  );
};

const filterRow = {
  display: 'flex',
  gap: '0.5rem',
  marginBottom: '1rem',
  alignItems: 'center'
};

const searchBox = {
  padding: '0.5rem',
  border: '1px solid #ccc',
  borderRadius: '4px',
  flex: 1
};

export default MaintenancePage;
