import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = () => {
  const [lease, setLease] = useState(null);
  const [file, setFile] = useState(null);

  const userId = JSON.parse(localStorage.getItem('user'))?.user_id;

  useEffect(() => {
    async function fetchLease() {
      try {
        const res = await axios.get(`/api/leases/user/${userId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setLease(res.data);
      } catch (err) {
        console.error('Lease fetch error:', err);
      }
    }
    if (userId) fetchLease();
  }, [userId]);

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/leases/${lease.lease_id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setLease(null);
    } catch (err) {
      console.error('Lease delete error:', err);
    }
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      const res = await axios.put(`/api/leases/${lease.lease_id}/file`, formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setLease(prev => ({ ...prev, lease_file_url: res.data.lease_file_url }));
    } catch (err) {
      console.error('File upload error:', err);
    }
  };

  if (!lease) return <p>No lease found.</p>;

  return (
    <div className="lease-card">
      <h3>Lease Info</h3>
      <p><strong>Property ID:</strong> {lease.property_id}</p>
      <p><strong>Start:</strong> {lease.start_date}</p>
      <p><strong>End:</strong> {lease.end_date}</p>
      <p><strong>Rent:</strong> ${lease.rent_amount}</p>
      <p><strong>Renewal Requested:</strong> {lease.renewal_requested ? 'Yes' : 'No'}</p>
      <p><strong>Lease File:</strong> {lease.lease_file_url || 'None'}</p>

      <input type="file" onChange={e => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload Lease File</button>
      <button onClick={handleDelete}>Delete Lease</button>
    </div>
  );
};

export default TenantLeaseCard;
