import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';
import TenantLeaseCard from '../components/TenantLeaseCard';
import TenantPayments from '../components/TenantPayments';
import LeasePhotosForm from '../components/LeasePhotosForm';
import DocumentsForm from '../components/DocumentsForm';

const TenantDashboard = ({ user, onLogout }) => {
  const [lease, setLease] = useState(null);
  const [reminders, setReminders] = useState([]);
  const [passcodes, setPasscodes] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
  if (!user?.id) return;

  api.get(`/user-reminders/${user.id}`).then(res => setReminders(res.data));
  api.get(`/smart-passcodes/${user.id}`).then(res => setPasscodes(res.data));
}, [user]);

  const handlePaymentClick = async () => {
    try {
      const res = await api.post('/payments/create-session');
      if (res.data?.url) {
        window.location.href = res.data.url;
      } else {
        console.error('‚ùå No Stripe URL returned:', res.data);
      }
    } catch (err) {
      console.error('‚ùå Payment error:', err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Tenant Portal</div>
        <div style={styles.navRight}>
          {location.pathname !== '/tenant/profile' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant/profile')}>
              Profile
            </button>
          )}
          {location.pathname !== '/tenant' && (
            <button style={styles.navButton} onClick={() => navigate('/tenant')}>
              Dashboard
            </button>
          )}
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name || 'Guest'}</h2>
        <p style={styles.role}>Role: {user?.role || 'tenant'}</p>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Tenant Profile</h3>
          <p><strong>Name:</strong> {user?.name || 'N/A'}</p>
          <p><strong>Email:</strong> {user?.email || 'N/A'}</p>
        </div>

        {user?.id && (
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>Lease Info</h3>
            <TenantLeaseCard userId={user.id} onLeaseLoaded={setLease} />
          </div>
        )}

        {user?.id && (
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>Payment History</h3>
            <TenantPayments userId={user.id} />
            <button style={styles.button} onClick={handlePaymentClick}>
              Make a Payment
            </button>
          </div>
        )}

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Lease Photos</h3>
          <LeasePhotosForm
            leaseId={lease?.lease_id}
            token={localStorage.getItem('token')}
            role={user.role}
          />
        </div>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Documents</h3>
          {lease?.lease_id ? (
            <DocumentsForm
              leaseId={lease.lease_id}
              userId={user.id}
              role={user.role}
            />
          ) : (
            <p>Loading lease...</p>
          )}
        </div>

        <div className="reminder-section">
  <h3>Reminders</h3>
  {reminders.map(r => (
    <div key={r.reminder_id} className="reminder-item">
      <strong>{r.type.toUpperCase()}:</strong> {r.message} ‚Äî{' '}
      {new Date(r.remind_at).toLocaleDateString()}
    </div>
  ))}
</div>

        <div style={styles.card}>
          <h3 style={styles.sectionTitle}>Smart Passcodes</h3>
          <ul style={styles.list}>
            {passcodes.map((pc, index) => (
              <li key={index}>Code: {pc.code} ‚Äî Expires: {pc.expiration_date}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: { backgroundColor: '#f0f2f5', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' },
  navbar: {
    background: '#0078D4', color: '#fff', display: 'flex',
    justifyContent: 'space-between', alignItems: 'center',
    padding: '1rem 2rem', boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
  },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: { background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer' },
  logoutButton: { background: '#dc3545', border: 'none', color: '#fff', padding: '0.5rem 1rem', borderRadius: '6px', cursor: 'pointer' },
  container: { padding: '2rem', display: 'flex', flexDirection: 'column', alignItems: 'center' },
  heading: { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' },
  role: { fontSize: '1rem', marginBottom: '2rem', color: '#666' },
  card: { backgroundColor: '#fff', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', padding: '1.5rem', width: '100%', maxWidth: '600px', marginBottom: '2rem' },
  sectionTitle: { fontSize: '1.25rem', marginBottom: '1rem', color: '#333', borderBottom: '1px solid #ddd', paddingBottom: '0.5rem' },
  button: { marginTop: '1rem', padding: '0.5rem 1rem', backgroundColor: '#0078D4', color: '#fff', border: 'none', borderRadius: '4px', cursor: 'pointer' },
  list: { listStyle: 'none', padding: 0, margin: 0 },
  link: { color: '#0078D4', textDecoration: 'none' }
};

export default TenantDashboard;
