import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AdminPanel() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')); // assumes user is stored on login

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      navigate('/unauthorized'); // redirect if not admin
    }
  }, [user, navigate]);

  return (
    <div style={pageStyle}>
      {/* Top navigation bar */}
      <nav style={navStyle}>
        <div style={navLeft}>üè¢ Admin Portal</div>
        <div style={navRight}>
          <button style={navButton} onClick={() => navigate('/admin-panel')}>Dashboard</button>
          <button style={logoutButton} onClick={() => navigate('/login')}>Logout</button>
        </div>
      </nav>

      {/* Main content */}
      <div style={containerStyle}>
        <h2 style={headingStyle}>Welcome, {user?.name}</h2>
        <p style={subHeadingStyle}>Role: {user?.role}</p>

        <div style={cardGrid}>
          <div style={card} onClick={() => navigate('/properties')}>
            <h3>üè† Properties</h3>
            <p>Manage property listings and details.</p>
          </div>
          <div style={card} onClick={() => navigate('/tenants')}>
            <h3>üë• Tenants</h3>
            <p>View and manage tenant accounts.</p>
          </div>
          <div style={card} onClick={() => navigate('/maintenance')}>
            <h3>üõ† Maintenance</h3>
            <p>Review and assign maintenance requests.</p>
          </div>
          <div style={card} onClick={() => navigate('/passcodes')}>
            <h3>üîë Passcodes</h3>
            <p>Access and manage smart passcodes.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

/* Styles */
const pageStyle = {
  backgroundColor: '#f4f6f8',
  minHeight: '100vh',
  fontFamily: 'Segoe UI, sans-serif'
};

const navStyle = {
  background: '#0078D4',
  color: '#fff',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '1rem 2rem',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
};

const navLeft = { fontWeight: 'bold', fontSize: '1.2rem' };
const navRight = { display: 'flex', gap: '1rem' };
const navButton = { background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer' };
const logoutButton = {
  background: '#dc3545',
  border: 'none',
  color: '#fff',
  padding: '0.5rem 1rem',
  borderRadius: '6px',
  cursor: 'pointer'
};

const containerStyle = { padding: '2rem', textAlign: 'center' };
const headingStyle = { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' };
const subHeadingStyle = { fontSize: '1rem', marginBottom: '2rem', color: '#666' };

const cardGrid = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
  gap: '1.5rem',
  marginTop: '2rem'
};

const card = {
  backgroundColor: '#fff',
  borderRadius: '10px',
  boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  padding: '1.5rem',
  cursor: 'pointer',
  transition: 'transform 0.2s ease',
  textAlign: 'center'
};
