import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const LandlordDashboard = ({ user, onLogout }) => {
  const [properties, setProperties] = useState([]);
  const [tenants, setTenants] = useState([]);
  const [payments, setPayments] = useState([]);
  const [maintenance, setMaintenance] = useState([]);
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [filter, setFilter] = useState('all');

  const navigate = useNavigate();

  useEffect(() => {
    if (!user?.id) return;

    Promise.all([
      api.get(`/properties/user?user_id=${user.id}`),
      api.get(`/leases/landlord-tenants/${user.id}`),
      api.get(`/payments/landlord/${user.id}`),
      api.get(`/maintenance/landlord/${user.id}`)
    ])
      .then(([propsRes, tenantsRes, paymentsRes, maintRes]) => {
        setProperties(propsRes.data);
        setTenants(tenantsRes.data);
        setPayments(paymentsRes.data);
        setMaintenance(maintRes.data);
      })
      .catch(err =>
        console.error("‚ùå Error loading landlord data:", err.response?.data || err.message)
      );
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout?.();
    navigate('/login');
  };

  // üî¢ Quick stats
  const totalProperties = properties.length;
  const totalTenants = tenants.length;
  const totalRentCollected = payments.reduce((sum, p) => sum + parseFloat(p.amount || 0), 0);

  // üõ†Ô∏è Update maintenance status
  const updateMaintenanceStatus = async (id, status) => {
    try {
      await api.patch(`/landlord/maintenance/${id}`, { status });
      const res = await api.get(`/landlord/maintenance/${user.id}`);
      setMaintenance(res.data);
    } catch (err) {
      console.error(`‚ùå Failed to update maintenance ${id}:`, err.response?.data || err.message);
    }
  };

  // üìä Aggregates
  const tenantsByProperty = tenants.reduce((acc, t) => {
    acc[t.property_id] = (acc[t.property_id] || 0) + 1;
    return acc;
  }, {});

  const rentByProperty = payments.reduce((acc, p) => {
    acc[p.property_id] = (acc[p.property_id] || 0) + parseFloat(p.amount || 0);
    return acc;
  }, {});

  const maintenanceByProperty = maintenance.reduce((acc, m) => {
    acc[m.property_id] = (acc[m.property_id] || 0) + 1;
    return acc;
  }, {});

  const formatCurrency = (amount) =>
    new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);

  const filteredMaintenance = maintenance.filter(m =>
    filter === 'all' ? true : m.status === filter
  );

  return (
    <div style={styles.page}>
      <nav style={styles.navbar}>
        <div style={styles.navLeft}>üè† Landlord Portal</div>
        <div style={styles.navRight}>
          <button style={styles.navButton} onClick={() => navigate('/landlord')}>Dashboard</button>
          <button style={styles.logoutButton} onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div style={styles.container}>
        <h2 style={styles.heading}>Welcome, {user?.name}</h2>
        <p style={styles.role}>Role: {user?.role}</p>

        {/* ‚úÖ Quick Stats Bar */}
        <div style={styles.statsBar}>
          <div style={styles.stat}><strong>üè† Properties:</strong> {totalProperties}</div>
          <div style={styles.stat}><strong>üë• Tenants:</strong> {totalTenants}</div>
          <div style={styles.stat}><strong>üí≥ Rent Collected:</strong> {formatCurrency(totalRentCollected)}</div>
        </div>

        <div style={styles.grid}>
          {/* Properties */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üè† Properties</h3>
            {properties.length === 0 ? (
              <p>No properties found.</p>
            ) : (
              <ul style={styles.list}>
                {properties.map(p => (
                  <li
                    key={p.property_id}
                    style={{ marginBottom: '1rem', cursor: 'pointer' }}
                    onClick={() => setSelectedProperty(selectedProperty === p.property_id ? null : p.property_id)}
                  >
                    <strong>{p.address}</strong> ‚Äî {p.city}, {p.state}
                    <div>üë• Tenants: {tenantsByProperty[p.property_id] || 0}</div>
                    <div>üí≥ Rent Collected: {formatCurrency(rentByProperty[p.property_id] || 0)}</div>
                    <div>üõ†Ô∏è Open Requests: {maintenanceByProperty[p.property_id] || 0}</div>
                    <div>
                      üìä Status: <span style={{
                        backgroundColor: p.status === 'active' ? '#22c55e' : '#f97316',
                        color: '#fff',
                        padding: '0.25rem 0.5rem',
                        borderRadius: '6px',
                        fontSize: '0.85rem'
                      }}>{p.status}</span>
                    </div>

                    {selectedProperty === p.property_id && (
                      <div style={styles.expandBox}>
                        <h4>Tenants</h4>
                        <ul>
                          {tenants.filter(t => t.property_id === p.property_id).map(t => (
                            <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                          ))}
                        </ul>
                        <h4>Payments</h4>
                        <ul>
                          {payments.filter(pay => pay.property_id === p.property_id).map(pay => (
                            <li key={pay.payment_id}>
                              {pay.tenant_name} ‚Äî {formatCurrency(pay.amount)} on {new Date(pay.date).toLocaleDateString()}
                            </li>
                          ))}
                        </ul>
                        <h4>Maintenance</h4>
                        <ul>
                          {maintenance.filter(m => m.property_id === p.property_id).map(m => (
                            <li key={m.request_id}>{m.type}: {m.description} ‚Äî {m.status}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Tenants */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üë• Tenants</h3>
            {tenants.length === 0 ? (
              <p>No tenants assigned.</p>
            ) : (
              <ul style={styles.list}>
                {tenants.map(t => (
                  <li key={t.user_id}>{t.name} ‚Äî Lease #{t.lease_id}</li>
                ))}
              </ul>
            )}
          </div>

          {/* Payments */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üí≥ Payments</h3>
            {payments.length === 0 ? (
              <p>No payments recorded.</p>
            ) : (
              <ul style={styles.list}>
                {payments.map(pay => (
                  <li key={pay.payment_id}>
                    {pay.tenant_name} ‚Äî {formatCurrency(pay.amount)} on {new Date(pay.date).toLocaleDateString()}
                  </li>
                ))}
              </ul>
            )}
          </div>

                    {/* Maintenance Requests */}
          <div style={styles.card}>
            <h3 style={styles.sectionTitle}>üõ†Ô∏è Maintenance Requests</h3>
            <div style={{ marginBottom: '1rem' }}>
              <button style={styles.filterButton} onClick={() => setFilter('all')}>All</button>
              <button style={styles.filterButton} onClick={() => setFilter('pending')}>Pending</button>
              <button style={styles.filterButton} onClick={() => setFilter('approved')}>Approved</button>
              <button style={styles.filterButton} onClick={() => setFilter('completed')}>Completed</button>
            </div>
            {filteredMaintenance.length === 0 ? (
              <p>No maintenance requests.</p>
            ) : (
              <ul style={styles.list}>
                {filteredMaintenance.map(m => (
                  <li key={m.request_id} style={{ marginBottom: '0.5rem' }}>
                    <strong>{m.type}:</strong> {m.description} ‚Äî 
                    Status: {m.status} ‚Äî 
                    {new Date(m.created_at).toLocaleDateString()}
                    <div style={{ marginTop: '0.5rem' }}>
                      {m.status !== 'approved' && (
                        <button style={styles.button} onClick={() => updateMaintenanceStatus(m.request_id, 'approved')}>
                          Approve
                        </button>
                      )}
                      {m.status !== 'completed' && (
                        <button style={styles.buttonSecondary} onClick={() => updateMaintenanceStatus(m.request_id, 'completed')}>
                          Complete
                        </button>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: 'linear-gradient(to bottom, #eef2f7, #f9fafb)',
    minHeight: '100vh',
    fontFamily: 'Segoe UI, sans-serif'
  },
  navbar: {
    background: '#2563eb',
    color: '#fff',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    position: 'sticky',
    top: 0,
    zIndex: 1000
  },
  navLeft: { fontWeight: 'bold', fontSize: '1.2rem' },
  navRight: { display: 'flex', gap: '1rem' },
  navButton: {
    background: 'transparent',
    border: '1px solid #fff',
    color: '#fff',
    cursor: 'pointer',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    transition: 'background 0.2s ease'
  },
  logoutButton: {
    background: '#dc3545',
    border: 'none',
    color: '#fff',
    padding: '0.5rem 1rem',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.2s ease'
  },
  container: { padding: '2rem', maxWidth: '1200px', margin: '0 auto' },
  heading: { fontSize: '2rem', marginBottom: '0.25rem', color: '#222' },
  role: { fontSize: '1rem', marginBottom: '2rem', color: '#666' },
  statsBar: {
    display: 'flex',
    justifyContent: 'space-around',
    backgroundColor: '#f3f4f6',
    padding: '1rem',
    borderRadius: '10px',
    marginBottom: '2rem',
    boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
  },
  stat: { fontSize: '1.1rem', color: '#333' },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '1.5rem'
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
    padding: '1.5rem',
    transition: 'transform 0.2s ease, box-shadow 0.2s ease'
  },
  sectionTitle: {
    fontSize: '1.25rem',
    marginBottom: '1rem',
    color: '#333',
    borderBottom: '1px solid #ddd',
    paddingBottom: '0.5rem'
  },
  list: { listStyle: 'none', padding: 0, margin: 0 },
  button: {
    backgroundColor: '#0078D4',
    color: '#fff',
    border: 'none',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    cursor: 'pointer',
    marginRight: '0.5rem',
    transition: 'background 0.2s ease'
  },
  buttonSecondary: {
    backgroundColor: '#6c757d',
    color: '#fff',
    border: 'none',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.2s ease'
  },
  filterButton: {
    backgroundColor: '#e5e7eb',
    border: 'none',
    padding: '0.4rem 0.8rem',
    borderRadius: '6px',
    cursor: 'pointer',
    marginRight: '0.5rem',
    transition: 'background 0.2s ease'
  },
  expandBox: {
    marginTop: '0.75rem',
    padding: '0.75rem',
    backgroundColor: '#f9fafb',
    borderRadius: '8px',
    boxShadow: 'inset 0 2px 6px rgba(0,0,0,0.05)'
  }
};

export default LandlordDashboard;
