// src/components/TenantPayments.jsx

import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function TenantPayments({ userId }) {
  const [payments, setPayments] = useState(null);

  useEffect(() => {
api.get(`/payment-history/user`)
      .then(res => setPayments(res.data))
      .catch(() => setPayments([]));
  }, [userId]);

  if (!payments) return <p>Loading payment history...</p>;
  if (payments.length === 0) return <p>No payments found.</p>;

  return (
    <div>
     <table style={tableStyle}>
  <thead>
    <tr>
      <th>Date</th>
      <th>Amount</th>
      <th>Status</th>
      <th>Late Fee</th>
      <th>Note</th>
    </tr>
  </thead>
  <tbody>
    {payments.map((payment) => (
      <tr key={payment.payment_id}>
        <td>{new Date(payment.paid_date).toLocaleDateString()}</td>
        <td>${parseFloat(payment.amount).toLocaleString()}</td>
        <td>{payment.status}</td>
        <td>{payment.late_fee_applied ? 'Yes' : 'No'}</td>
        <td>{payment.note || '—'}</td>
      </tr>
    ))}
  </tbody>
</table>
    </div>
  );
}
