import React from 'react';

const SmartPasscode = ({ passcode, onRevoke, onDelete }) => {
  const formatDate = (dt) => dt ? new Date(dt).toLocaleString() : 'â€”';

  return (
    <div style={card}>
      {/* Header with status + expiry */}
      <div style={cardHeader}>
        <span style={badge(passcode.is_active)}>
          {passcode.is_active ? 'Active' : 'Revoked'}
        </span>
        <span style={expires}>
          Expires: {formatDate(passcode.expires_at)}
        </span>
      </div>

      {/* Body with details */}
      <div style={cardBody}>
        <p><strong>Lease:</strong> {passcode.lease_id}</p>
        <p><strong>User:</strong> {passcode.user_id}</p>
        <p><strong>Code:</strong> {passcode.passcode}</p>
      </div>

      {/* Actions */}
      <div style={actions}>
        {passcode.is_active && (
          <button style={btnRevoke} onClick={() => onRevoke(passcode.passcode_id)}>
            Revoke
          </button>
        )}
        <button style={btnDelete} onClick={() => onDelete(passcode.passcode_id)}>
          Delete
        </button>
      </div>
    </div>
  );
};

/* ðŸŽ¨ Styles */
const card = {
  background: '#fff',
  borderRadius: '12px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.06)',
  padding: '1.2rem',
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem'
};

const cardHeader = { display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const badge = (active) => ({
  padding: '0.3rem 0.7rem',
  borderRadius: '6px',
  fontSize: '0.85rem',
  fontWeight: '600',
  color: '#fff',
  backgroundColor: active ? '#27ae60' : '#c0392b'
});
const expires = { fontSize: '0.85rem', color: '#888' };

const cardBody = { fontSize: '0.95rem', lineHeight: '1.4' };

const actions = { display: 'flex', gap: '0.5rem' };
const btnRevoke = {
  background: '#f39c12',
  color: '#fff',
  border: 'none',
  padding: '0.5rem 1rem',
  borderRadius: '6px',
  cursor: 'pointer',
  fontWeight: '600'
};
const btnDelete = {
  background: '#e74c3c',
  color: '#fff',
  border: 'none',
  padding: '0.5rem 1rem',
  borderRadius: '6px',
  cursor: 'pointer',
  fontWeight: '600'
};

export default SmartPasscode;
