import React, { useState } from 'react';
import axios from 'axios';

const SmartPasscodeForm = ({ leases, users, onCreated }) => {
  const [leaseId, setLeaseId] = useState('');
  const [userId, setUserId] = useState('');
  const [expiresAt, setExpiresAt] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/smart-passcodes', {
        lease_id: leaseId,
        user_id: userId,
        expires_at: expiresAt || null
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      onCreated(res.data.passcode);
      setLeaseId('');
      setUserId('');
      setExpiresAt('');
    } catch (err) {
      console.error('‚ùå Passcode create error:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={form}>
      <h3 style={title}>Generate Smart Passcode üîê</h3>

      <label style={label}>Lease</label>
      <select value={leaseId} onChange={(e) => setLeaseId(e.target.value)} style={input}>
        <option value="">Select Lease</option>
        {leases.map(l => (
          <option key={l.lease_id} value={l.lease_id}>
            Lease #{l.lease_id} ‚Äì Property {l.property_id}
          </option>
        ))}
      </select>

      <label style={label}>User</label>
      <select value={userId} onChange={(e) => setUserId(e.target.value)} style={input}>
        <option value="">Select User</option>
        {users.map(u => (
          <option key={u.user_id} value={u.user_id}>
            {u.name || `User #${u.user_id}`}
          </option>
        ))}
      </select>

      <label style={label}>Expiry (optional)</label>
      <input
        type="datetime-local"
        value={expiresAt}
        onChange={(e) => setExpiresAt(e.target.value)}
        style={input}
      />

      <button type="submit" style={button}>Generate</button>
    </form>
  );
};

/* üé® Styles */
const form = {
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  backgroundColor: '#fff',
  padding: '1.5rem',
  borderRadius: '10px',
  boxShadow: '0 2px 6px rgba(0,0,0,0.08)'
};

const title = { margin: 0, fontSize: '1.2rem', fontWeight: '600' };
const label = { fontSize: '0.9rem', fontWeight: '500' };
const input = {
  padding: '0.5rem',
  border: '1px solid #ccc',
  borderRadius: '6px',
  fontSize: '0.9rem'
};
const button = {
  padding: '0.6rem 1rem',
  border: 'none',
  borderRadius: '6px',
  backgroundColor: '#3498db',
  color: '#fff',
  fontWeight: '600',
  cursor: 'pointer'
};

export default SmartPasscodeForm;
