import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TenantLeaseCard = ({ user, onLeaseLoaded }) => {
  const [lease, setLease] = useState(null);

  useEffect(() => {
    const fetchLease = async () => {
      try {
        console.log("üß© Fetching lease for user:", user.id);

        const token = localStorage.getItem('token');

        const res = await axios.get(`/api/leases/user/${user.id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        console.log("üì¶ Lease API Response:", res.data);

        // üî• Normalize backend data to avoid crashes
        const normalizedLease = res.data
          ? {
              lease_id: res.data.lease_id,
              property_id: res.data.property_id,
              start_date: res.data.start_date,
              end_date: res.data.end_date,
              rent_amount: res.data.rent_amount || 0, // backend does not send it ‚Üí fallback
              lease_file_url: res.data.lease_file_url || null,
              renewal_requested: res.data.renewal_requested || 0,
            }
          : null;

        setLease(normalizedLease);

        if (onLeaseLoaded) onLeaseLoaded(normalizedLease);

      } catch (err) {
        console.error("‚ùå Lease fetch error", err.response?.data || err.message);
        setLease(null);
        if (onLeaseLoaded) onLeaseLoaded(null);
      }
    };

    fetchLease();
  }, [user, onLeaseLoaded]);

  return (
    <div style={cardStyle}>
      {lease ? (
        <>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Property ID:</strong> {lease.property_id}</p>
          <p><strong>Start:</strong> {new Date(lease.start_date).toLocaleDateString()}</p>
          <p><strong>End:</strong> {new Date(lease.end_date).toLocaleDateString()}</p>
          <p><strong>Rent:</strong> ${lease.rent_amount}</p>
          <p><strong>Status:</strong> {lease.renewal_requested ? 'Renewal requested' : 'Active'}</p>
          {lease.lease_file_url && (
            <p><strong>Lease File:</strong> {lease.lease_file_url}</p>
          )}
        </>
      ) : (
        <p>No lease found.</p>
      )}
    </div>
  );
};

const cardStyle = {
  border: '1px solid #ccc',
  borderRadius: '6px',
  padding: '1rem',
  backgroundColor: '#fff',
  marginBottom: '2rem'
};

export default TenantLeaseCard;
